import React from "react";
import DynamicFormSection from '../sections/dynamicForm/dynamicFormSection'
import { Helpers, PageMain } from "core";
import NotificationSystem from 'react-notification-system'
import { GetUrl, HandleError } from '../utils/generals'

export default class DynamicFormPage extends React.Component
{
    // Constructor
    constructor(props)
    {
        super(props);
        this.notificationSystem = React.createRef();
        this.state = {
            FormData: null,
            isLoading: true
        };
        this.requestId = this.props.match.params.requestId;
        this.formId = this.props.match.params.formId;
        this.formTitle = this.props.match.params.formTitle;
    }

    componentDidMount()
    {
        Helpers.CallServer('/api/FRQST/Form/GetFormFields/',
                            {
                                requestId: this.requestId,
                                formId: this.formId
                            },
                            successfulResponse =>
                            {
                                this.setState({ FormData: successfulResponse.data });
                            },
                            errorResponse => {
                                return HandleError(Helpers, this.notificationSystem, errorResponse);
                            },
                            () =>
                            {
                                this.setState({ isLoading: false });
                            })
    }

    render()
    {
        return this.state.FormData != null && (<>
                    <PageMain
                        title={ this.formTitle }
                        isLoading={ this.state.isLoading }
                        icon="list-details"
                        context={ this }
                        topButtons={[{ title: "فهرست فرم ها", icon: "list-details", color: "outline-success", url: GetUrl("requestFormListPage/" + this.requestId) } ]}
                        footerLinks={[
                        { href: "/", text: "صفحه اصلی" },
                        { href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" },
                        ]}>
                            <DynamicFormSection formData={ this.state.FormData }
                                                title={ 'جزئیات ' + this.formTitle }
                                                requestId={ this.requestId }
                                                formId={ this.formId } />
                    </PageMain>
                    <NotificationSystem ref={ this.notificationSystem } />
                </>)
    }
}
