import React, { Component } from "react";
import NotificationSystem from "react-notification-system";
import {
  Backward,
  DownloadBlobFile,
  HandleError,
} from "../../../utils/generals";
import {
  DatePicker,
  DropDown,
  FileUploader,
  Helpers,
  PageMain,
  SimpleGrid,
  TextBox,
} from "core";
import Button from "../../../componet/button";
import constants from "../../../utils/constants";

export default class BillingReportsListPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      updateKey: 1,
      gridHeaders: [],
      reportTypes: null,
      gridFilterOption: null,
    };

    this.PersonTypes = [
      constants.PersonNumberType.Real,
      constants.PersonNumberType.Legal,
      constants.PersonNumberType.Foreign,
    ];

    this.personNumber = {
      Title: null,
      MaxLength: null,
    };

    this.currentSelectedReport = null;
    this.notificationSystem = React.createRef();
  }

  onSearch = () => {};

  onRemoveFilter = () => {
    this.setState({
      reportTypeId: null,
      importTypeId: null,
      personNumberTypeId: null,
      number: null,
      fromDate: null,
      toDate: null,
      accountNumber: null,
      billingReportFile: null,
      updateKey: this.state.updateKey + 1,
    });
  };

  downloadSampleFile = () => {};

  onPersonNumberTypeSelected = (item) => {
    if (item == null || item.value == null || item.value == "") {
      this.personNumber = {
        Title: null,
        MaxLength: null,
      };
      return;
    }
    switch (item.value) {
      case constants.PersonNumberType.Real.value:
        this.personNumber = {
          Title: "کد ملی",
          MaxLength: 10,
        };
        break;
      case constants.PersonNumberType.Legal.value:
        this.personNumber = {
          Title: "شناسه ملی",
          MaxLength: 11,
        };
        break;
      case constants.PersonNumberType.Foreign.value:
        this.personNumber = {
          Title: "کد فراگیر اتباع",
          MaxLength: 12,
        };
        break;
      default:
        this.personNumber = null;
        break;
    }
  };

  downloadBillingReport = () => {
    this.setState({ isLoading: true });
    Helpers.CallServer(
      `/api/amls/PersonInfo/PersonInfoReport`,
      {},
      (successfulResponse) =>
        DownloadBlobFile(successfulResponse, "billingReportFile.xlsx"),
      (errorResponse) =>
        HandleError(Helpers, this.notificationSystem, errorResponse),
      () =>
        this.setState({
          isLoading: false,
        }),
      "GET",
      "blob"
    );
  };
  render() {
    return (
      <>
        <PageMain
          title="گزارش های صورتحساب ها"
          icon="list-search"
          isLoading={this.state.isLoading}
          footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
          key={this.state.updateKey}
          topButtons={
            this.state.importTypeId == 2
              ? [
                  {
                    title: "دانلود فایل نمونه",
                    url: this.downloadBillingReport,
                    color: "outline-success",
                    icon: "notebook",
                  },
                ]
              : null
          }
        >
          <div className="card">
            <div className="card-body">
              {/* {this.state.reportTypes && ( */}
              <>
                <div className="row">
                  <DropDown
                    context={this}
                    mdCol={4}
                    lgCol={4}
                    name="importTypeId"
                    title="نوع ایمپورت"
                    options={[
                      { title: "تکی", value: 1, label: "تکی" },
                      { title: "گروهی", value: 2, label: "گروهی" },
                    ]}
                    required
                  />
                  <DropDown
                    context={this}
                    mdCol={4}
                    lgCol={4}
                    name="reportTypeId"
                    title="نوع گزارش"
                    options={
                      this.state.importTypeId == 1
                        ? [
                            {
                              title: "صورتحساب استاندارد مبارزه با پولشویی",
                              value: 1,
                              label: "صورتحساب استاندارد مبارزه با پولشویی",
                            },
                            {
                              title: "صورتحساب امور مالیاتی",
                              value: 2,
                              label: "صورتحساب امور مالیاتی",
                            },
                          ]
                        : [
                            {
                              title:
                                "صورتحساب استاندارد مبارزه با پولشویی- گروهی",
                              value: 3,
                              label:
                                "صورتحساب استاندارد مبارزه با پولشویی- گروهی",
                            },
                            {
                              title: "صورتحساب امور مالیاتی-گروهی",
                              value: 4,
                              label: "صورتحساب امور مالیاتی-گروهی",
                            },
                          ]
                    }
                    required
                  />
                  <DropDown
                    context={this}
                    mdCol={4}
                    lgCol={4}
                    name="personNumberTypeId"
                    title="نوع شخص"
                    options={this.PersonTypes}
                    changeHandler={(e) => this.onPersonNumberTypeSelected(e)}
                  />
                </div>
                <div className="row">
                  <TextBox
                    context={this}
                    name="number"
                    title={
                      this.personNumber.Title == null
                        ? "شماره"
                        : this.personNumber.Title
                    }
                    minLength={1}
                    maxLength={
                      this.personNumber.MaxLength == null
                        ? 1
                        : this.personNumber.MaxLength
                    }
                    validateHandler={[Helpers.MinMaxLengthValidator]}
                    mdCol={4}
                    lgCol={4}
                  />
                  <DatePicker
                    context={this}
                    mdCol={4}
                    lgCol={4}
                    name="fromDate"
                    title="بازه تراکنش از"
                    useEpoch
                  />
                  <DatePicker
                    context={this}
                    mdCol={4}
                    lgCol={4}
                    name="toDate"
                    title="تا تاریخ"
                    useEpoch
                  />
                </div>
                <div className="row">
                  <TextBox
                    context={this}
                    name="accountNumber"
                    title="شماره حساب"
                    minLength={1}
                    maxLength={25}
                    validateHandler={[Helpers.MinMaxLengthValidator]}
                    mdCol={4}
                    lgCol={4}
                  />
                  {this.state.importTypeId == 2 ? (
                    <FileUploader
                      context={this}
                      name="billingReportFile"
                      title="ایمپورت فایل"
                      validExtensions={[".xlsx"]}
                    />
                  ) : null}
                </div>
                <div className="row">
                  <div className="col">
                    <Button
                      buttonClass={`btn btn-outline-primary m-1 styleButton`}
                      text="جستجو"
                      onclick={() => this.onSearch()}
                    />
                    <Button
                      buttonClass={`btn btn-outline-danger m-1 styleButton`}
                      text="حذف فیلتر"
                      onclick={() => this.onRemoveFilter()}
                    />

                    <Button
                      buttonClass={`btn btn-outline-success m-1 styleButton`}
                      text="خروجی اکسل"
                      onclick={() => this.downloadSampleFile()}
                    />
                  </div>
                </div>
                <br />
              </>
              {/* )} */}
              {/* {this.state.selectedReport && ( */}
              <SimpleGrid
                context={this}
                minHeightTableWrapper={constants.MinGridHeight}
                filterBgColor="primary"
                hideExportButton
                name="billingReportList"
                title="گزارش های صورتحساب ها"
                // title={`گزارش های صورتحساب ها- ${this.state.selectedReport.title}`}

                listApiUrl="/api/amls/bi/DepositeStatusCoding/SearchReport"
                // extraItemsSentToFetchData={{
                //   reportTypeId: this.state.selectedReport.value,
                // }}
                headers={this.state.gridHeaders}
                enableAutoFetch
                filterOptions={this.state.gridFilterOption}
                notificationSystem={this.notificationSystem}
              />
              {/* )} */}
            </div>
            <div className="card-footer">
              <div className="d-flex">
                <a
                  className="btn btn-outline-secendery ms-auto"
                  onClick={(e) => Backward(e)}
                  href="/"
                  title="بازگشت به صفحه قبل"
                >
                  بازگشت
                  <i className="ti ti-arrow-left ms-1"></i>
                </a>
              </div>
            </div>
          </div>
        </PageMain>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}
