import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl } from "../../utils/generals";
import Constants from '../../utils/constants'

export default class PersonnelInfListPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: false
        };

        this.notificationSystem = React.createRef();
    }

  // Events.
  Backward(e)
  {
    e.preventDefault();
    history.back();
  }

  render()
  {
    return (
        <>
            <PageMain context={ this } title="لیست اشخاص " icon="users" isLoading={ this.state.isLoading } footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
            topButtons={[
                {
                title: "افزودن شخص",
                url: GetUrl("personnelInfAddPage"),
                color: "outline-primary",
                icon: "user-plus",
                },
            ]}>
            <div className="card">
                <div className="card-body">
                    <SimpleGrid context={ this } dataKey="id" minHeightTableWrapper={ Constants.MinGridHeight } hideExportButton
                        filterBgColor="primary" name="name" title="فهرست گروه" notificationSystem={ this.notificationSystem } disabledPaging
                        enableAutoFetch listApiUrl="/api/amls/RegionInfo/GetAllRegionsInfo"
                        editPageUrl={ GetUrl("RegionInfoEditPage" ) }
                        deleteApiUrl="/api/amls/RegionGroup/DeleteGroup"
                        headers={[
                            { title: "کد منطقه", name: "regionCode" },
                            { title: "نام منطقه", name: "regionName" },
                            { title: "نوع منطقه", name: "regionTypeName" },
                            { title: "تاریخ اعتبار از", name: "expirationDateFromStr" },
                        ]} />
                </div>
                <div className="card-footer">
                    <div className="d-flex">
                        <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => this.Backward(e) } href="/" title="بازگشت به صفحه قبل">
                        بازگشت
                        <i className="ti ti-arrow-left ms-1"></i>
                        </a>
                    </div>
                    </div>
                </div>
            </PageMain>
            <NotificationSystem ref={ this.notificationSystem } />
        </>
    );
  }
}
