import { PageMain, Helpers } from "core";
import React, { Component } from "react";
import NotificationSystem from "react-notification-system";

export default class BaseDataPage extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isLoading: false
        };

        this.notificationSystem = React.createRef();
    }

    render() {
        return (
            <>
                <PageMain context={ this } title="اطلاعات پایه" icon="database" isLoading={ this.state.isLoading }
                    footerLinks={[{ href: "/", text: "صفحه اصلی" }]}>

                    
                    <Card context={ this } title="اطلاعات پایه" name="crdbaseDareReport" statusColor="primary" hasDefaultSaveButton
                            submitUrl="" hasBackButton icon="database" onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)}>

                                <DropDown context={ this } name="reportTypeId" title="نوع گزارش" options={ [] } />


                                <DatePicker context={ this } name="fromDate" title="از تاریخ" useEpoch required />
                                <DatePicker context={ this } name="toDate" title="تا تاریخ" useEpoch required />

                        </Card>



                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        )
    }
}
