import React from "react";
import { PageMain, Helpers, Card, TextBox, DatePicker } from "core";
import NotificationSystem from "react-notification-system";

class LawyerInformationPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tabNumber: 0,
      isLoading: false,
      updateKey: 1,
    };
    this.tabNumber = props.tabNumber;
    this.pageInfo = {
      title: "اطلاعات وکیل",
      icon: "circle-plus",
    };
    this.notificationSystem = React.createRef();
  }
  render() {
    return (
      <>
        <Card
          context={this}
          title={this.pageInfo.title}
          icon="file-text"
          //  submitUrl={
          //    this.isNew
          //      ? "/api/Insp/UserRole/Post"
          //       : "/api/Insp/UserRole/Update"
          //    }
          hasBackButton={true}
          statusColor="primary"
          onSubmitError={(error) => handleError(error, this.notificationSystem)}
          hasDefaultSaveButton={true}
          onSubmitSuccess={(e) => this.onSubmitSuccess(e)}
          key={this.state.updateKey}
        >
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="نام"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="شماره سند"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="نام خانوادگی"
            notificationSystem={this.notificationSystem}
            required
          />
          <DatePicker
            context={this}
            name="expirationDateFrom"
            title="تاریخ وکالتنامه"
            useEpoch
            lgCol={6}
            mdCol={12}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="شماره ملی/شماره فراگیر"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="شماره وکالتنامه"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="شماره دفتر خانه اسناد رسمی"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="خلاصه موضوع وکالتنامه"
            notificationSystem={this.notificationSystem}
            required
          />
        </Card>

        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}

export default LawyerInformationPage;
