import { Component } from "react";
import React from "react";
import { SimpleGrid, PageMain } from "core";
import NotificationSystem from "react-notification-system";
import Constants from '../../utils/constants';
import { Backward, GetUrl } from "../../utils/generals";

const Null = "null";

export default class RegisterStrLogListPage extends Component
{
    constructor(props)
    {
        super(props);
        this.state = {
            isLoading: false
        };

        this.notificationSystem = React.createRef();

        this.requestId = this.props.match.params.requestId;
        if(this.requestId == undefined)
            this.requestId =  Null;

        this.getFilterOptions = this.getFilterOptions.bind(this);
    }

    getFilterOptions()
    {
        var filterOptions = [];

        filterOptions.push({ type: 'auto-complete', name: 'requestorPersonCode', title: 'پرسنل', lookupApiUrl: "/api/amls/DistributionOfIndicator/SearchPersonels" });
        filterOptions.push({ type: "datepicker", name: "startDate", title: "از تاریخ درخواست", useEpoch: true });
        filterOptions.push({ type: "datepicker", name: "endDate", title: "تا تاریخ درخواست", useEpoch: true });
        if(this.requestId ==  Null)
        {
            filterOptions.push({ type: 'text', name: 'customerNumber', title: 'شماره مشتری' });
            filterOptions.push({ type: 'text', name: 'personUniqueCode', title: 'کد ملی/شناسه ملی/ کد اتباع' });
        }

        return filterOptions;
    }

    // Events.
    render()
    {
        return (
            <>
                <PageMain context={ this } title="فهرست لاگ درخواست ثبت STR" icon="note" isLoading={ this.state.isLoading } footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                    topButtons={[
                        {
                            title: "فهرست درخواست ثبت STR پولشویی",
                            url: GetUrl("registerStrListPage"),
                            color: "outline-primary",
                            icon: "list-search",
                        }
                    ]} >
                    <div className="card">
                        <div className="card-body">
                            <SimpleGrid context={ this } minHeightTableWrapper={ Constants.MinGridHeight } hasSearchBox hideExportButton enableAutoFetch
                                filterBgColor="primary" name="grRegisterStrList" title="جزئیات لاگ درخواست های ثبت STR پولشویی" notificationSystem={ this.notificationSystem }
                                listApiUrl="/api/amls/RegisterSTRLog/SearchRegisterSTRLog" extraItemsSentToFetchData={{
                                    Id: this.requestId,
                                    Name: 1
                                }}
                                badgeOnCells={[
                                    {
                                      fieldName: "statusName",
                                      color: (x) => {
                                        switch (x) {
                                        case Constants.STRLogState.Done.Title:
                                            return "green";
                                          case Constants.STRLogState.RepititiveRequestLessThanOneYear.Title:
                                            return "red";
                                          default:
                                            return "yellow"
                                        }
                                      },
                                    },
                                  ]}
                                headers={[
                                    { title: "کد پرسنل درخواست دهنده", name: "requestorPersonCode" },
                                    { title: "نام پرسنل درخواست دهنده", name: "requestorPersonName" },
                                    { title: "تاریخ درخواست", name: "requestDateStr" },
                                    { title: "وضعیت", name: "statusName" },
                                    { title: "شماره مشتری", name: "customerNumber" },
                                    { title: "کد ملی/شناسه ملی/کد اتباع", name: "personUniqueCode" },

                                ]}
                                moreInfoLinks={[
                                    { title:'صورتحساب 68 ستونی مشتری', icon:'receipt-2', enable: this.isRequestDone, url: GetUrl('registerStrPersonBillings/#replaceWith_requestId#') },
                                    { title:'نمایش STR', icon:'license', enable: this.isRequestDone, url: GetUrl('strListPage/#replaceWith_requestId#') },
                                ]}
                                filterOptions={ this.getFilterOptions() } />
                        </div>
                        <div className="card-footer">
                            <div className="d-flex">
                                <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => Backward(e) } href="/" title="بازگشت به صفحه قبل">
                                    بازگشت
                                <i className="ti ti-arrow-left ms-1"></i>
                                </a>
                            </div>
                        </div>
                    </div>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
