import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import Constants from "../../../utils/constants";
import { GetUrl, HandleError } from "../../../utils/generals";

export default class PaiedLoanListPage extends Component {
  constructor(props) {
    super(props);

    this.state = { isLoading: true };

    this.notificationSystem = React.createRef();
    this.companyName = null;

    this.companyInfoId = this.props.match.params.companyInfoId;
    this.companyLoanAccountId = this.props.match.params.companyLoanAccountId;
  }

  // Events.
  componentDidMount()
  {
      Helpers.CallServer('/api/FRQST/PaiedLoan/GetPaidLoanListPageData/' + this.companyInfoId,
      { },
      successfulResponse =>
      {
        if(successfulResponse.data.legalCompanyInfo != null)
          this.companyName = successfulResponse.data.legalCompanyInfo.companyName;
      },
      errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
      () =>
      { this.setState({ isLoading: false }) }, 'GET');
  }

  render() {
    return this.companyName != null && (
      <>
        <PageMain context={ this } title={ "فهرست ریز تسهیلات پرداختی" + (this.companyName == null ? "" : " (" + this.companyName + ")")}
          icon="list" isLoading={ this.state.isLoading }
          topButtons={[
            {
              title: "افزودن ریز تسهیلات پرداختی",
              url: GetUrl("paiedLoanAddPage/" + this.companyInfoId + '/' + this.companyLoanAccountId),
              color: "outline-success",
              icon: "cash-banknote",
            },
          ]}
          footerLinks={[{ href: "/", text: "صفحه اصلی" }]}>
          <SimpleGrid context={ this } dataKey="id" minHeightTableWrapper={ Constants.MinGridHeight } filterBgColor="primary" name="grid" title="جزئیات ریز تسهیلات دریافتی"
            notificationSystem={ this.notificationSystem } enableAutoFetch listApiUrl="/api/FRQST/PaiedLoan/SearchPaiedLoans"
            extraItemsSentToFetchData={{
              LegalCompanyInfoId: this.companyInfoId,
              CompanyLoanAccountId: this.companyLoanAccountId
            }}
            moreInfoLinks={[
              { title:'تکمیل فرم ها', icon:'eye', url: GetUrl('paiedLoanViewPage/#replaceWith_id#/#replaceWith_legalCompanyInfoId#/#replaceWith_companyLoanAccountId#') }
            ]}
            headers={[
              { title: "نام و نام خانوادگی ", name: "firstLastName" },
              { title: "شماره مشتری", name: "customerNumber" },
              { title: "شماره ملی", name: "nationalCode" },
              { title: "شماره تسهیلات", name: "loanNumber" },
              { title: "مبلغ تسهیلات", name: "loanAmountStr" },
              { title: "نام تسهیلات", name: "loanName" },
              { title: "نرخ تسهیلات", name: "rate" },
              { title: "تعداد ماه باز پرداخت", name: "monthNumber" },
              { title: "مانده اعتبار", name: "creditBalanceStr" },
            ]}
            filterOptions={[
              { type: "text", title: "نام و نام خانوادگی ", name: "firstLastName" },
              { type: "text", title: "شماره مشتری", name: "customerNumber" },
              { type: "text", title: "شماره ملی", name: "nationalCode" },
              { type: "text", title: "شماره تسهیلات", name: "loanNumber" },
              { type: "text", title: "مبلغ تسهیلات", name: "loanAmount" }
            ]}/>
        </PageMain>
        <NotificationSystem ref={ this.notificationSystem } />
      </>
    );
  }
}
