import { PageMain,Table, Card,SimpleGrid } from "core";
import react from 'react';
import React, { Component } from 'react';

import NotificationSystem from 'react-notification-system';

class FacilityTypeListSection extends React.Component {
    
    constructor(props){
        super(props);

       this.state = {  } 
       this.notificationSystem = react.createRef();
       this.onDataError = this.onDataError.bind(this);
      }
  
      onDataError(error)
      {
          var response = error.response;
  
          console.log('Response: ', error);
          if(response.status == 400 || response.status == 500)
              Helpers.DisplayErrorMessage(this.notificationSystem, null, response.data.message);
          else
              Helpers.DisplayErrorMessage(this.notificationSystem, null, 'بروز خطا کد پاسخ : '+ response.status);
      }
    render() { 
        return (
              <Card title="نمایش لیست درخواست" context={this}>

                   <SimpleGrid context={this} name="grid" title="لیست شرح درخواست" 
                    listApiUrl="/api/FRQST/FacilityType/GetList"
                   notificationSystem={this.notificationSystem}
                   headers={[{ title: '  شرح درخواست', name: 'name' }]}
                    dataKey="id"
                    editPageUrl="/facilityTypeEditPage"
                    metaData={{ totalCount: 2, pageSize: 15, page: 1 }}
                    onDataError={ this.onDataError }
                    enableAutoFetch
                    
                />

              <div style={{ textAlign: "left" }}>
                        <a
                          href="/"
                          className="btn btn-outline-secondry col-1 mx-1 mt-3 mb-2"
                          icon="arrow-back"
                          style={{ color: "#808080" }}
                        >
                          بازگشت
                          <i
                            className="ti ti-arrow-narrow-left mx-1 me-1"
                            style={{ color: "#808080" }}
                          />
                        </a>
                      </div>
                      <NotificationSystem ref={ this.notificationSystem } />
              </Card>
        );
    }
}
export default FacilityTypeListSection
