import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from "../utils/generals";
import Constants from '../utils/constants'

const CustomerTypes =
[
    { label: "حقیقی", title: "حقیقی", value: Constants.CustomerType.Real },
    { label: "حقوقی", title: "حقوقی", value: Constants.CustomerType.Legal },
    { label: "اتباع", title: "اتباع", value: Constants.CustomerType.Foreign },
]

export default class ManagementOfSuspiciousTransactionsAndOperationsListPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: false
        };

        this.notificationSystem = React.createRef();
    }

    // Events.
    render()
    {
        return (
            <>
                <PageMain context={ this } title="مدیریت معاملات و عملیات مشکوک" icon="list-search" isLoading={ this.state.isLoading } footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                topButtons={[
                    // {
                    // title: "افزودن آستانه مقرر",
                    // url: GetUrl("prescribedThresholdAddPage"),
                    // color: "outline-primary",
                    // icon: "layers-difference",
                    // }
                ]}>
                <div className="card">
                    <div className="card-body">
                        <SimpleGrid context={ this } dataKey="nationalNumber" minHeightTableWrapper={ Constants.MinGridHeight } hasSearchBox={ false } hideExportButton
                        filterBgColor="primary" name="crdsusList" title="جزئیات مدیریت معاملات و عملیات مشکوک" notificationSystem={ this.notificationSystem }
                        enableAutoFetch listApiUrl="/api/amls/bi/RolesResult/SearchRolesResult"
                        headers={[
                            { title: "نام مشتری", name: "customerName" },
                            { title: "شماره مشتری", name: "customerNumber" },
                            { title: "کد/شناسه ملی", name: "nationalNumber" },
                            { title: "موارد مشکوک", name: "foundRoles" }
                        ]}
                        filterOptions={[
                            { type: 'select', name: 'customerTypeId', title: 'نوع مشتری', selectOptions: CustomerTypes },
                            { type: 'text', name: 'codeId', title: 'کد/شناسه ملی' },
                        ]} />
                    </div>
                    <div className="card-footer">
                        <div className="d-flex">
                            <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => this.Backward(e) } href="/" title="بازگشت به صفحه قبل">
                            بازگشت
                            <i className="ti ti-arrow-left ms-1"></i>
                            </a>
                        </div>
                        </div>
                    </div>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
