import React from "react";
import { PageMain, Helpers, Card, TextBox, DropDown,DatePicker } from "core";
import NotificationSystem from "react-notification-system";
import { hasOptions } from "../../utils/selectOptionsUtils";

class LicensingExchangesPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tabNumber: 0,
      isLoading: false,
      updateKey: 1,
    };
    this.tabNumber = props.tabNumber;
    this.pageInfo = {
      title:"مجوز صرافی ها (بانک مرکزی)",
      icon: "circle-plus",
    };
    this.notificationSystem = React.createRef();
  }
  render() {
    return (
      <>

        <Card
          context={this}
          title={this.pageInfo.title}
          icon="file-text"
          //  submitUrl={
          //    this.isNew
          //      ? "/api/Insp/UserRole/Post"
          //       : "/api/Insp/UserRole/Update"
          //    }
          hasBackButton={true}
          statusColor="primary"
          onSubmitError={(error) => handleError(error, this.notificationSystem)}
          hasDefaultSaveButton={true}
          onSubmitSuccess={(e) => this.onSubmitSuccess(e)}
          key={this.state.updateKey}
        >

          <DropDown
            context={this}
            lgCol={6}
            mdCol={12}
            title="مجوز صرافی"
            name="group"
            notificationSystem={this.notificationSystem}
            options={hasOptions()}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="شناسه ملی"
            notificationSystem={this.notificationSystem}
            required
          />
           <DatePicker
            context={this}
            lgCol={6}
            mdCol={12}
            name="expirationDateFrom"
            title="تاریخ اعتبار از "
            useEpoch
            required 
            />
            <DatePicker
            context={this}
            lgCol={6}
            mdCol={12}
            name="expirationDateFrom"
            title="تا"
            useEpoch
            required 
            />            
           <DatePicker
            context={this}
            lgCol={6}
            mdCol={12}
            name="expirationDateFrom"
            title="تاریخ اخذ مجوز"
            useEpoch
            required 
            />            
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="شماره مجوز"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="نام صرافی"
            notificationSystem={this.notificationSystem}
            required
          />
        </Card>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}

export default LicensingExchangesPage;
