import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError, GetDropDownValues } from "../../utils/generals";
import Constants from '../../utils/constants'

export default class IndicatorsResultPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            dropDowns: null,
            isLoading: false
        };

        this.notificationSystem = React.createRef();
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/CustomerActivityLevel/GetPageData',
            { },
            successfulResponse =>
            {
                this.setState({
                    dropDowns: {
                        personTypes: GetDropDownValues(Constants.DropDowns.PersonTypeId, successfulResponse.data),
                        jobTypes: GetDropDownValues(Constants.DropDowns.JobTypeId, successfulResponse.data),
                        taxStates: GetDropDownValues(Constants.DropDowns.TaxStateId, successfulResponse.data)
                    }
                });
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    Backward(e)
    {
        e.preventDefault();
        history.back();
    }

    render()
    {
        return (
            <>
                <PageMain context={ this } title="مشاهده نتایج اجرای شاخص‌ها" icon="list-search" isLoading={ this.state.isLoading } footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                topButtons={[]}>
                <div className="card">
                    <div className="card-body">
                        {
                            this.state.dropDowns && 
                                <SimpleGrid context={ this } 
                                dataKey="id" 
                                minHeightTableWrapper={ Constants.MinGridHeight } 
                                hasSearchBox={ false } 
                                hideExportButton
                                filterBgColor="primary" name="crdRegionList" 
                                title="مشاهده نتایج اجرای شاخص‌ها"
                                notificationSystem={ this.notificationSystem }
                                enableAutoFetch 
                                listApiUrl="/api/amls/CustomerActivityLevel/SearchCustomersActivityLevel" 
                                headers={[
                                    { title: "تاریخ تهیه گزارش", name: "personTypeName" },
                                    { title: "از تاریخ", name: "expirationDateFromStr" },
                                    { title: "تا تاریخ", name: "expirationDateFromStr" },
                                    { title: "دانلود فایل", name: "expirationDateFromStr" },
                                ]}
                                filterOptions={[
                                    { type: 'datepicker', name: 'personTypeId', title: 'از تاریخ', },
                                    { type: 'datepicker', name: 'jobTypeId', title: 'تا تاریخ',},
                                    { type: 'number', name: 'taxStateId', title: 'از مبلغ'},
                                    { type: 'number', name: 'taxStateId', title: 'تا مبلغ'},
                                    { type: 'select', name: 'taxStateId', title: 'نوع مشتری', selectOptions: this.state.dropDowns.taxStates },
                                    { type: 'number', name: 'taxStateId', title: 'کد شناسه / ملی' },
                                    { type: 'text', name: 'taxStateId', title: 'شناسه مشتری'},
                                    { type: 'select', name: 'taxStateId', title: 'نوع مشتری', selectOptions: this.state.dropDowns.taxStates },
                                    
                                    { type: "auto-complete", name: "regionCode", title: "منطقه", lookupApiUrl: "/api/amls/bi/RolesResult/SearchRegions" },

                                    { type: 'select', name: 'taxStateId', title: 'شاخص تخصیص یافته', selectOptions: this.state.dropDowns.taxStates },
                                    { type: 'select', name: 'taxStateId', title: 'سایر شاخص ها', selectOptions: this.state.dropDowns.taxStates },
                                ]} />
                        }
                    </div>
                    <div className="card-footer">
                        <div className="d-flex">
                            <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => this.Backward(e) } href="/" title="بازگشت به صفحه قبل">
                            بازگشت
                            <i className="ti ti-arrow-left ms-1"></i>
                            </a>
                        </div>
                        </div>
                    </div>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
