import { Component } from "react";
import React from "react";
import { PageMain, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import PersonnelInfSection from '../../sections/personnelInfSection'
import { GetUrl, HandleError } from "../../utils/generals";
import Constants from '../../utils/constants'
export default class PersonnelInfAddPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: true,
            dropDowns: null
        };

        this.notificationSystem = React.createRef();
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/PersonnelInfo/GetAddPageData',
            { },
            successfulResponse =>
            {
                var temp = successfulResponse.data.filter(drp => drp.id == Constants.DropDowns.PersonStateId);
                var states = (temp == null || temp.length <= 0 ? [] : temp.values);

                temp = successfulResponse.data.filter(drp => drp.id == Constants.DropDowns.PersonTypeId);
                var personTypes = (temp == null || temp.length <= 0 ? [] : temp.values);;

                temp = successfulResponse.data.filter(drp => drp.id == Constants.DropDowns.PersonNationalityId);
                var personNationalities = (temp == null || temp.length <= 0 ? [] : temp.values);;

                this.setState({
                    dropDowns: {
                        states: states,
                        personTypes: personTypes,
                        personNationalities: personNationalities
                    }
                })
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    render()
    {
        return this.state.dropDowns && (
            <>
                <PageMain title="افزودن اطلاعات شخص" icon="user-plus" footerLinks={[{ href: "/", text: "صفحه اصلی" }]} isLoading={ this.state.isLoading }
                topButtons={[
                    {
                        title: "لیست اشخاص",
                        url: GetUrl("personnelInfListPage"),
                        color: "outline-primary",
                        icon: "users",
                    },
                ]}>
                <PersonnelInfSection record={ null } dropDowns={ this.state.dropDowns } />
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
