export function jobOptions() {
  let array = [];
  return array.concat([
    { title: "", value: "1", label: "براساس خوداظهاری " },
    { title: "", value: "2", label: "براساس جستجو کارشناس بانک در سامانه‌های موجود" },
  ]);
}

export function yesNoOptions() {
  let array = [];
  return array.concat([
    { title: "", value: 1, label: "بله" },
    { title: "", value: 0, label: "خیر" },
  ]);
}

export function hasOptions() {
  let array = [];
  return array.concat([
    { title: "", value: 1, label: "دارد" },
    { title: "", value: 0, label: "ندارد" },
  ]);
}

export function groupOptions() {
  let array = [];
  return array.concat([
    { title: "", value: 1, label: "1" },
    { title: "", value: 2, label: "2" },
    { title: "", value: 3, label: "3" },
  ]);
}

export function almsOptions() {
  let array = [];
  return array.concat([
    { title: "", value: 1, label: "مبادلات غیرمتعارف" },
    { title: "", value: 2, label: "احتمال فعالیت غیرمجاز ارزی" },
    { title: "", value: 3, label: "مشتری مزبور دارای ارتباطات مالی نامتعارف با موسسات خیریه و مردم نهاد می‌باشد که احتمال سوءاستفاده از حساب به صورت غیرمستقیم وجود دارد" },
    { title: "", value: 4, label: "منشا سرمایه مشخص نمی‌باشد و شخص از ارائه اسناد و مستندات مربوطه خودداری می‌نماید" },
    { title: "", value: 5, label: "مبادلاتی که بدون اهداف اقتصادی باشد و شخص فاقد زمینه فعالیت و شغلی مشخصی می‌باشد " },
    { title: "", value: 6, label: "کلاهبرداری " },
    { title: "", value: 7, label: "شرط بندی " },
    { title: "", value: 8, label: "تعهدات کلان و ایفا نشده بانکی و سوابق متعدد و مکرر در خصوص چک برگشتی" },
    { title: "", value: 9, label: "هویت جعلی می‌باشد یا مراحل احراز هویت ناتمام بوده و شخص به نحو مشکوکی از ادامه احراز هویت امتناع نماید" },
    { title: "", value: 10, label: "رفتار و نحوه برخورد شخص مشکوک است" },
    { title: "", value: 11, label: "ارتباط مالی شخص با گروهها و اشخاص فعال در حوزه قاچاق کالا" },
    { title: "", value: 12, label: "ارتباط مالی شخص با گروهها و اشخاص فعال در حوزه مواد مخدر" },
    { title: "", value: 13, label: "سایر" },
  ]);
}

export function personNumberTypeOptions() {
  let array = [];
  return array.concat([
    { title: "حقیقی", value: 1, label: "حقیقی" },
    { title: "حقوقی", value: 2, label: "حقوقی" },
    { title: "اتباع", value: 3, label: "اتباع" },
  ]);
}
