import React, { Component } from 'react';
import { Card, TextBox,Helpers } from "core";
import NotificationSystem from 'react-notification-system';
import { HandleError } from '../../utils/generals'

class FacilityTypeSection extends React.Component {
    constructor(props) {
        super(props);
        this.itemId = this.props.itemId;
        this.isNew = this.itemId == null;
        this.state = {};
        this.notificationSystem = React.createRef();

    }
 componentDidMount()
    {
      
            if (!this.isNew) {
                const _this = this;
                Helpers.CallServer(
                  "/api/FRQST/FacilityType/Get",
                  
                  (res) => {
                    _this.setState({ item: res });
                  },
                  (errorResponse) => HandleError(Helpers, this.notificationSystem, errorResponse),
                  () => _this.setState({ isLoading: false }),
                  
                );
              }
    //   this.setState({custInfo : this.item.RealCustomer , AddInfo : this.item.Address });
    }


    render() {
        return (
            // <PageMain title=" فرم" icon="users" context={this} footerLinks={[{ href: "/", text: "صفحه اصلی" },{ href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" }]}>
 
 <Card
          context={this}
           title="شرح درخواست"
          icon="info-circle" 
          hasBackButton={true}
          hasDefaultSaveButton
          statusColor="success"
          submitUrl={
        this.isNew
          ? "/api/FRQST/FacilityType/Post" 
          : "/api/FRQST/FacilityType/Update"
      }
        >
    <TextBox
            context={this}
            name="name"
            title="شرح درخواست"
            required
            
          /> 
        <NotificationSystem ref={ this.notificationSystem } />
        </Card>

        );
    }
}


export default FacilityTypeSection
