import { Component } from "react";
import React from "react";
import NotificationSystem from "react-notification-system";
import LegalCompanyLoanInfoSection from "../../../sections/projects/legalCompanyInfoSection";
import { PageMain, Helpers } from "core";
import { GetUrl, HandleError } from "../../../utils/generals";

export default class LegalCompanyInfoAddPage extends Component {
  constructor(props) {
    super(props);
    this.state = { isLoading: true };

    this.notificationSystem = React.createRef();
  }

  // Events.
  componentDidMount()
  {
      Helpers.CallServer('/api/FRQST/LegalCompanyInfo/GetCurretnUserInfo',
      { },
      successfulResponse =>
      {
          this.setState({ personnelBranchInfo: successfulResponse.data });
      },
      errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
      () => this.setState({ isLoading: false }), 'GET')
  }
  
  render() {
    return (
      <>
        <PageMain context={ this } title="افزودن اطلاعات شرکت" icon="building-community" isLoading={ this.state.isLoading }
          topButtons={[
            {
              title: "فهرست شرکت ها",
              url: GetUrl("legalCompanyInfoListPage"),
              color: "outline-success",
              icon: "list",
            }]} footerLinks={[{ href: "/", text: "صفحه اصلی" }]}>
            <LegalCompanyLoanInfoSection formData={ null } personnelBranchInfo={ this.state.personnelBranchInfo } />
        </PageMain>
        <NotificationSystem ref={ this.notificationSystem } />
      </>
    );
  }
}
