import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid } from "core";
import NotificationSystem from "react-notification-system";
import Constants from '../../../utils/constants';
import { GetUrl } from '../../../utils/generals'
export default class RequestBillingListPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = { isLoading: false };

        this.notificationSystem = React.createRef();
    }

    // Events.
    downloadBillings(record)
    {
        if(record == null || record.id == null || record.id == "")
        {
          Helpers.DisplayErrorMessage(this.notificationSystem, null, 'محاسبات مربوط به شعبه پیدا نشد');
          return;
        }
        window.open(GetUrl(`downlaodPersonBillings/${record.id}`), "_target");
    }

    showHideMessage(item)
    {
        console.log("ShowHideMessage: ", item);
        return true;
    }

    showMessage(item)
    {
        console.log("ShowMessage: ", item);
    }

    render()
    {
        return (
            <>
                <PageMain context={ this } title="فهرست درخواست صورتحساب ها" icon="list-search" isLoading={ this.state.isLoading }
                footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                topButtons={[
                    {
                        title: "افزودن درخواست صورتحساب",
                        url: GetUrl("requestBillingAddPage"),
                        color: "outline-primary",
                        icon: "receipt",
                    },
                ]}>
                    <div className="card">
                        <div className="card-body">
                            <SimpleGrid context={ this } minHeightTableWrapper={ Constants.MinGridHeight } hasSearchBox={ false } hideExportButton
                                filterBgColor="primary" name="crdrqstList" title="جزئیات فهرست درخواست صورتحساب ها" notificationSystem={ this.notificationSystem } enableAutoFetch
                                listApiUrl="/api/amls/Request/SearchRequests" 
                                headers={[
                                            { title: "کد پرسنل", name: "personCode" },
                                            { title: "نام پرسنل", name: "personName" },
                                            { title: "تاریخ درخواست", name: "requestDateStr" },
                                            { title: "نوع درخواست", name: "requestTypeName" },
                                            { title: "وضعیت", name: "statusName" },
                                            { title: "کد خطا", name: "errorTrackingCode" },
                                    ]}
                                    badgeOnCells={[
                                        {
                                            fieldName: "statusName",
                                            color: (x) => {
                                            switch (x) {
                                                case Constants.RequestState.Registerd.Title:
                                                    return "gray"
                                                case Constants.RequestState.InProcess.Title:
                                                    return "yellow";
                                                case Constants.RequestState.Done.Title:
                                                    return "green";
                                                case Constants.RequestState.Error.Title:
                                                    return "red";
                                                case Constants.RequestState.Closed.Title:
                                                    return "blue";
                                            }}}]}
                                filterOptions={[
                                        { type: 'auto-complete', name: 'personCode', title: 'پرسنل', lookupApiUrl: "/api/amls/DistributionOfIndicator/SearchPersonels" },
                                        //{ type: 'select', name: 'requestTypeId', title: 'نوع درخواست', selectOptions: [] }
                                ]}
                                moreInfoLinks={[
                                        { title:'صورتحساب شخص', icon:'receipt-2',url: GetUrl('personBillingListPage/#replaceWith_id#') },
                                        { title:'دانلود فایل صورتحساب', icon:'receipt-2', func: this.downloadBillings, url: "" },
                                        { title:'پیام سیستم', icon:'message', func: this.showMessage, url: "", divider: true, enable: this.showHideMessage },
                                      ]}
                                />
                        </div>
                        <div className="card-footer">
                            <div className="d-flex">
                                <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => this.Backward(e) } href="/" title="بازگشت به صفحه قبل">
                                    بازگشت
                                <i className="ti ti-arrow-left ms-1"></i>
                                </a>
                            </div>
                        </div>
                    </div>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
