import React from "react";
import { PageMain, Helpers, Card, TextBox, DropDown } from "core";
import NotificationSystem from "react-notification-system";
import { hasOptions } from "../../utils/selectOptionsUtils";

class EBankPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tabNumber: 0,
      isLoading: false,
      updateKey: 1,
    };
    this.tabNumber = props.tabNumber;
    this.pageInfo = {
      title: "اطلاعات مربوط بانکداری نوین / مجازی",
      icon: "circle-plus",
    };
    this.notificationSystem = React.createRef();
  }
  render() {
    return (
      <>

        <Card
          context={this}
          title={this.pageInfo.title}
          icon="file-text"
          //  submitUrl={
          //    this.isNew
          //      ? "/api/Insp/UserRole/Post"
          //       : "/api/Insp/UserRole/Update"
          //    }
          hasBackButton={true}
          statusColor="primary"
          onSubmitError={(error) => handleError(error, this.notificationSystem)}
          hasDefaultSaveButton={true}
          onSubmitSuccess={(e) => this.onSubmitSuccess(e)}
          key={this.state.updateKey}
        >
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="شماره حساب"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="نام و نام خانوادگی دریافت کننده دسته چک"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="شماره همراه دریافت پیامک"
            notificationSystem={this.notificationSystem}
            required
          />
          <TextBox
            context={this}
            lgCol={6}
            mdCol={12}
            name="age"
            title="شماره ملی دریافت کننده دسته چک"
            notificationSystem={this.notificationSystem}
            required
          />

          <DropDown
            context={this}
            lgCol={6}
            mdCol={12}
            title="موبایل بانک"
            name="group"
            notificationSystem={this.notificationSystem}
            options={hasOptions()}
            required
          />

          <DropDown
            context={this}
            lgCol={6}
            mdCol={12}
            title="اینترنت بانک"
            name="group"
            notificationSystem={this.notificationSystem}
            options={hasOptions()}
            required
          />
          <DropDown
            context={this}
            lgCol={6}
            mdCol={12}
            title="دسته چک"
            name="group"
            notificationSystem={this.notificationSystem}
            options={hasOptions()}
            required
          />
        </Card>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}

export default EBankPage;
