import React from "react";
import { AlertBox, Card, TextBox, Helpers, DatePicker, AutoComplete, DropDown, Delimiter, SimpleGrid } from "core";
import NotificationSystem from 'react-notification-system'
import { HandleError } from '../../utils/generals'
import Constants from '../../utils/constants'
import { IsLegalPersonNationalIdValid } from '../../utils/nationalIdentification'

const CustomerNumberControlName = "customerNumber";

export default class LegalCustomerInBranchSection extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            CompanyTypes: null,
            CanSelectBranch: Helpers.IsInRole(Constants.Roles.SuperAdmin) ||
                             Helpers.IsInRole(Constants.Roles.SystemAdmin) ||
                             Helpers.IsInRole(Constants.Roles.Customer) ||
                             Helpers.IsInRole(Constants.Roles.CustomerAssistant),
            updateKey: 1,
            loading :false,
            RequestStates: null,
            ButtonChangeRequestState: false
        };
        this.notificationSystem = this.props.notificationSystem;
        this.changeHandler = this.changeHandler.bind(this);
        this.onSubmitSuccess = this.onSubmitSuccess.bind(this);
        this.onBeforeSubmit = this.onBeforeSubmit.bind(this);
        this.OnGetCustomerInfo = this.OnGetCustomerInfo.bind(this);
        this.ChangeRequestState = this.ChangeRequestState.bind(this);
        this.BranchIsDisabled = this.BranchIsDisabled.bind(this);
        this.ChangeRequestBranch = this.ChangeRequestBranch.bind(this);
    }

    componentDidMount()
    {
        Helpers.CallServer('/api/FRQST/LegalCustomer/GetLegalCustomerPageData',
                            { },
                            successfulResponse =>
                            {
                                var branchInfo;

                                if(this.props.readOnly && this.props.legalCustomerInfo != null)
                                    branchInfo = {
                                        code: this.props.legalCustomerInfo.branchCode,
                                        name: this.props.legalCustomerInfo.branchName
                                    }
                                else
                                    branchInfo = successfulResponse.data.branchInfo;
                                this.setState({
                                    CompanyTypes: successfulResponse.data.companyTypes,
                                    cityId: (this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.cityId),
                                    branchCode: (branchInfo != null ? branchInfo.code : null),
                                    BranchInfo: branchInfo,
                                    updateKey: this.state.updateKey + 1,
                                    RequestStates: successfulResponse.data.requestStates
                                });
                            },
                            errorResponse => {
                                return HandleError(Helpers, this.notificationSystem, errorResponse);
                            },
                            () =>
                            {
                                // Do tasks if it is neccessary
                                console.log('Done');
                            }, 'GET');
    }

    changeHandler(item)
    {
        if(item == null)
            return;
        this.setState({ branchName: item.label });
    }

    onSubmitError(error)
    {
        var response = error.response;

        if(response.status == 400 || response.status == 500)
            Helpers.DisplayErrorMessage(this.notificationSystem, null, response.data.message);
        else
            Helpers.DisplayErrorMessage(this.notificationSystem, null, 'بروز خطا کد پاسخ : '+ response.status);
        return true;
    }

    onSubmitSuccess(resposne)
    {
        this.setState({
            TrackingCode: resposne.data.data,
            updateKey: this.state.updateKey + 1,
            companyName: null,
            registerDate: null,
            companyTypeId: null,
            registerationNumber: null,
            nationalCode: null,
            registerLocationName: null,
            customerNumber: null,
            cellphoneNumber: null,
            phoneNumber: null,
            cityId: null,
            address: null,
            postalCode: null,
            branchCode: null,
            requestStatusId: null,
            usageReason: null
        });
        Helpers.DisplaySuccessMessage(this.notificationSystem, 'اطلاعات با موفقیت ذخیره شد');
    }

    onBeforeSubmit()
    {
        var legalNationalId = this.state.nationalCode;
        var isValid = IsLegalPersonNationalIdValid(legalNationalId);

        if(isValid == false)
            Helpers.DisplayErrorMessage(this.notificationSystem, null, 'شناسه ملی شرکت صحیح نمی باشد');
        return isValid;
    }

    OnGetCustomerInfo()
    {
        var customerNumber = this.state[CustomerNumberControlName];

        if(customerNumber == null || customerNumber == "")
        {
            Helpers.DisplayWarningMessage(this.notificationSystem, null, 'شماره مشتری را وارد نمایید');
            return;
        }
        Helpers.DisplayWarningMessage(this.notificationSystem, null, "این امکان در حال ساخت می باشد");
        return;
        Helpers.CallServer('/api/FRQST/LegalCustomer/GetCustomerInfo/' + customerNumber,
                            { },
                            successfulResponse =>
                            {
                                if(successfulResponse.data.isSuccess)
                                {
                                    Helpers.DisplaySuccessMessage(this.notificationSystem, "اطلاعات کاربر دریافت شد");
                                    // console.log("Customer Information: ", successfulResponse.data.resultData);
                                }
                                else
                                    Helpers.DisplayErrorMessage(this.notificationSystem, null, successfulResponse.data.message);
                            },
                            errorResponse => {
                                return HandleError(Helpers, this.notificationSystem, errorResponse);
                            },
                            () =>
                            {
                                this.setState({ loading: false });
                            }, 'GET');
    }

    ChangeRequestState()
    {
        var requestStateId = this.state["requestStatusId"];
        var comment = this.state['comment'];

        if(requestStateId == null || requestStateId == '')
        {
            Helpers.DisplayWarningMessage(this.notificationSystem, null, 'وضعیت درخواست را انتخاب نمایید');
            return;
        }
        this.setState({ ButtonChangeRequestState: true });
        Helpers.CallServer('/api/FRQST/Request/ChangeRequestState',
                            {
                                RequestId: this.props.requestId,
                                RequestStatusId: requestStateId,
                                Comment: comment
                            },
                            successfulResponse =>
                            {
                                this.setState({
                                    comment: null,
                                    updateKey: this.state.updateKey + 1
                                });
                                Helpers.DisplaySuccessMessage(this.notificationSystem, successfulResponse.message);                                
                            },
                            errorResponse => {
                                return HandleError(Helpers, this.notificationSystem, errorResponse);
                            },
                            () =>
                            {
                                this.setState({ ButtonChangeRequestState: false })                                
                            });
    }

    ChangeRequestBranch()
    {
        var branchCode = this.state["branchCode"];

        if(branchCode == null || branchCode == '')
        {
            Helpers.DisplayWarningMessage(this.notificationSystem, null, 'شعبه را انتخاب نمایید');
            return;
        }
        this.setState({ ButtonChangeRequestState: true });
        Helpers.CallServer('/api/FRQST/Request/ChangeRequestBranch',
                            {
                                RequestId: this.props.requestId,
                                BranchCode: branchCode
                            },
                            successfulResponse =>
                            {
                                this.setState({
                                    comment: null,
                                    updateKey: this.state.updateKey + 1
                                });
                                Helpers.DisplaySuccessMessage(this.notificationSystem, successfulResponse.message);                                
                            },
                            errorResponse => {
                                return HandleError(Helpers, this.notificationSystem, errorResponse);
                            },
                            () =>
                            {
                                this.setState({ ButtonChangeRequestState: false })                                
                            });
    }

    BranchIsDisabled()
    {
        if(this.props.readOnly)
        {
            if(Helpers.IsInRole(Constants.Roles.SystemAdmin))
                return false;
            return true;
        }
        return !this.state.CanSelectBranch;
    }

    render() {
        return (
            <>
            <Card
                title='مشخصات درخواست'
                context={ this }
                submitUrl={ this.props.readOnly == false ? "/api/FRQST/LegalCustomer/RegisterLegalCustomerRequest" : "" }
                hasBackButton={ true }
                hasDefaultSaveButton= { this.props.readOnly ? false: true }
                statusColor="success"
                notificationSystem={ this.notificationSystem }
                updateKey={ this.state.updateKey }
                onSubmitError={ this.onSubmitError }
                onSubmitSuccess={ this.onSubmitSuccess }
                onBeforeSubmit={ this.onBeforeSubmit }>   

                    <TextBox context={ this }
                                name={ CustomerNumberControlName }
                                required
                                title="شماره مشتری"
                                minLength={ 1 }
                                maxLength={ 10 }
                                validateHandler={[ Helpers.MinMaxLengthValidator, Helpers.NumberTypeValidator ]}
                                readOnly={ this.props.readOnly }
                                leftButton={{icon:'users', text:'استعلام',  color:'info', onclick:this.OnGetCustomerInfo, loading:this.state.loading }} 
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.customerNumber } />
                              
                    <Delimiter/>
                    <TextBox context={ this }
                                name="companyName"
                                required
                                title="نام شرکت"
                                readOnly={ this.props.readOnly }
                                minLength={ 3 }
                                maxLength={ 64 }
                                validateHandler={ Helpers.MinMaxLengthValidator }
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.companyName } />

                        <DatePicker context={ this }
                                    name="registerDate"
                                    title="تاریخ ثبت شرکت"
                                    required
                                    readOnly={ this.props.readOnly }
                                    initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.registerDate } />
                        <DropDown context={ this }
                                name="companyTypeId"
                                title="نوع شرکت"
                                disabled={ this.props.readOnly }
                                options={ this.state.CompanyTypes == null ? [] : this.state.CompanyTypes }
                                required
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.companyTypeId } />
                        <TextBox context={ this }
                                name="registerationNumber"
                                required
                                title="شماره ثبت شرکت"
                                readOnly={ this.props.readOnly }
                                minLength={ 1 }
                                maxLength={ 15 }
                                validateHandler={[ Helpers.MinMaxLengthValidator, Helpers.NumberTypeValidator ]}
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.registerationNumber } />

                        <TextBox context={ this }
                                name="nationalCode"
                                required title="شناسه ملی شرکت" 
                                readOnly={ this.props.readOnly }
                                minLength={ 5 }
                                maxLength={ 11 }
                                validateHandler={[ Helpers.MinMaxLengthValidator, Helpers.NumberTypeValidator ]}
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.nationalCode } />
                        <TextBox context={ this }
                                name="registerLocationName"
                                title="محل ثبت شرکت"
                                required
                                readOnly={ this.props.readOnly }
                                validateHandler={ Helpers.MinMaxLengthValidator }
                                minLength={ 3 }
                                maxLength={ 64 }
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.registerLocationName } />

                        <TextBox context={ this }
                                name="cellphoneNumber"
                                title="تلفن همراه "
                                required
                                regex={ Helpers.MobileRegexPattern }
                                validateHandler={ Helpers.RegexValidator }
                                readOnly={ this.props.readOnly }
                                hint="شماره موبایل طبق نمونه '09XXXXXXXXX' وارد شود"
                                isNumber
                                minLength={ 11 }
                                maxLength={ 11 }
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.cellphoneNumber } />
                        <TextBox context={ this }
                                name="phoneNumber"
                                title="تلفن"
                                required
                                hint="شمار تلفن طبق نمونه '021xxxxxxxx' وارد شود"
                                isNumber
                                validateHandler={[ Helpers.NumberTypeValidator, Helpers.MinMaxLengthValidator ]}
                                readOnly={ this.props.readOnly }
                                minLength={ 5 }
                                maxLength={ 11 }
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.phoneNumber } />
                        <AutoComplete context={ this }
                                name="cityId"
                                title="شهر"
                                required
                                notificationSystem={ this.notificationSystem }
                                disabled={ this.props.readOnly }
                                remoteUrl="/api/FRQST/CityState/SearchCityAndProvince"
                                externalData={this.props.readOnly
                                    ? [{
                                        label: (this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.cityName) + ' - ' + (this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.stateName),
                                        value: (this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.cityId) }]
                                    : []} /> 
                        <TextBox context={ this }
                                name="officeAddress"
                                title="آدرس دفتر مرکزی شرکت"
                                required
                                readOnly={ this.props.readOnly }
                                minLength={ 5 }
                                maxLength={ 256 }
                                validateHandler={ Helpers.MinMaxLengthValidator }
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.address } />
                        <TextBox context={ this }
                                name="factoryAddress"
                                title="آدرس محل فعالیت/کارخانه"
                                readOnly={ this.props.readOnly }
                                minLength={ 5 }
                                maxLength={ 256 }
                                validateHandler={ Helpers.MinMaxLengthValidator }
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.address } />
                        <TextBox context={ this }
                                name="postalCode"
                                title="کد پستی"
                                required
                                validateHandler={[ Helpers.NumberTypeValidator, Helpers.MinMaxLengthValidator ]}
                                isNumber
                                readOnly={ this.props.readOnly }
                                minLength={ 5 }
                                maxLength={ 15 }
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.postalCode } />

                        <TextBox context={ this }
                                name="latinName"
                                title="نام لاتین شرکت"                             
                                readOnly={ this.props.readOnly }
                                validateHandler={ Helpers.MinMaxLengthValidator }
                                minLength={ 5 }
                                maxLength={ 64 }
                                isLTR
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.latinName } />
                                
                        <TextBox context={ this }
                                name="webSiteUrl"
                                title="آدرس سایت"
                                readOnly={ this.props.readOnly }
                                validateHandler={ Helpers.MinMaxLengthValidator }
                                minLength={ 5 }
                                maxLength={ 64 }
                                isLTR
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.webSiteUrl } />

                        <TextBox context={ this }
                                name="email"
                                title="پست الکترونیکی"       
                                readOnly={ this.props.readOnly }
                                regex={ Helpers.EmailRegexPattern }
                                validateHandler={[ Helpers.MinMaxLengthValidator, Helpers.RegexValidator ]}
                                minLength={ 5 }
                                maxLength={ 64 }
                                isLTR
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.email } />

                        {
                            (this.props.readOnly && Helpers.IsInRole(Constants.Roles.SystemAdmin)
                                ? <div className="col-12" />
                                : null)
                        }
                        <AutoComplete context={ this }
                                updateKey={ this.state.updateKey }
                                name="branchCode"
                                title="شعبه"
                                required
                                notificationSystem={ this.notificationSystem }
                                disabled={ this.BranchIsDisabled() } 
                                remoteUrl="/api/FRQST/LegalCustomer/GetBranches"
                                changeHandler={ this.changeHandler } 
                                externalData={this.state.BranchInfo == null
                                    ? []
                                    : [{ label: this.state.BranchInfo.name, value: this.state.BranchInfo.code }]} />
                        {
                            (this.props.readOnly && Helpers.IsInRole(Constants.Roles.SystemAdmin)
                                ?
                                    <div className="col-md-6 col-lg-4 my-3">
                                        <label style={{ "marginTop": "7%" }}>
                                            <button type="button" className="btn btn-primary align-bottom"  style={ { width: '150px', height: '40px' } } onClick={ this.ChangeRequestBranch } disabled={ this.state.ButtonChangeRequestState }>تغییر شعبه</button>
                                        </label>
                                    </div>
                                : null)
                        }
                        {
                            this.props.readOnly && Helpers.IsInRole(Constants.Roles.SystemAdmin)
                                ? <div className="col-12" />
                                : null
                        }
                        {
                        (Helpers.IsInRole(Constants.Roles.SuperAdmin) || Helpers.IsInRole(Constants.Roles.SystemAdmin)
                            ?
                                <DropDown context={ this }
                                    name='requestStatusId'
                                    title='وضعیت درخواست'
                                    options={ (this.state.RequestStates == null ? [] : this.state.RequestStates) }
                                    initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.requestStatusId.toString() }
                                    required />
                            : null)
                        }
                        {
                            (this.props.readOnly && (Helpers.IsInRole(Constants.Roles.SuperAdmin) || Helpers.IsInRole(Constants.Roles.SystemAdmin))
                            ?
                                <>
                                <div className="col-md-6 col-lg-4 my-3">
                                    <label style={{ "marginTop": "7%" }}>
                                        <button type="button" className="btn btn-primary align-bottom"  style={ { width: '150px', height: '40px' } } onClick={ this.ChangeRequestState } disabled={ this.state.ButtonChangeRequestState }>تغییر وضعیت</button>
                                    </label>
                                </div>
                                <div className="row">
                                    <div className="col-12">
                                        <TextBox context={ this }
                                                    name="comment"
                                                    title="توضیحات"
                                                    multiline
                                                    lgCol={12}
                                                    validateHandler={ Helpers.MinMaxLengthValidator }
                                                    minLength={ 3 }
                                                    maxLength={ 512 } />
                                    </div>
                                </div>
                                </>
                            : null)
                        }
                        <TextBox context={ this }
                                name="usageReason"
                                title="شرح درخواست"
                                multiline
                                required
                                lgCol={12}
                                readOnly={ this.props.readOnly }
                                minLength={ 3 }
                                maxLength={ 256 }
                                validateHandler={ Helpers.MinMaxLengthValidator }
                                initialValue={ this.props.legalCustomerInfo == null ? null : this.props.legalCustomerInfo.usageReason } />
                        {
                            this.props.requestId == null
                                ? null
                                : 
                                <SimpleGrid context={ this }
                                name="grid"
                                title="تاریخچه تغییر وضعیت درخواست"
                                enableAutoFetch
                                listApiUrl={ '/api/FRQST/Request/GetRequestStateChangeHistory' }
                                extraItemsSentToFetchData={ { Id: this.props.requestId } }
                                disabledPaging
                                headers={[
                                    { title: "نام کاربر تغییر دهنده", name: "userName" },
                                    { title: "وضعیت جدید درخواست", name: "requestStateName" },
                                    { title: 'توضیحات', name: 'comment' },
                                    { title: "تاریخ تغییر", name: "solarDate" }
                                ]}
                                hideExportButton
                                minHeightTableWrapper={ 200 }
                                badgeOnCells={[
                                    {
                                        fieldName: "requestStateName",
                                        color: (x) => {
                                        switch (x) {
                                            case Constants.RequestStatus.RequestRejected.Label:
                                                case Constants.RequestStatus.Closed.Label:
                                                    return "red";
                                                case Constants.RequestStatus.WaitingToFillFixedFormsByCustomer.Label:
                                                case Constants.RequestStatus.WaitingToCheckOrAssignFormsByCreditBranchManager.Label:
                                                case Constants.RequestStatus.WaitingToFillBranchFormsByCustomer.Label:
                                                case Constants.RequestStatus.WaitingToCheckRequestByBranchManager.Label:
                                                case Constants.RequestStatus.WaitingToCheckOrAssignFormsByRegionManager.Label:
                                                case Constants.RequestStatus.WaitingToFillRegionManagerFormsByCustomer.Label:
                                                case Constants.RequestStatus.WaitingToCheckOrAssignFormsByCenter.Label:
                                                case Constants.RequestStatus.WaitingToFillCenterFormsByCustomer.Label:
                                                case Constants.RequestStatus.WaitingToCheckOrAssignFormsByAdminCommercialCredits.Label:
                                                case Constants.RequestStatus.WaitingToFillAdminCommercialCreditsFormsByCustomer.Label:    
                                                    return "yellow";
                                                case Constants.RequestStatus.RequestApproved.Label:
                                                    return "green";
                                                default:
                                                    return "gray"
                                        }
                                        },
                                    },
                                    ]}
                                notificationSystem={ this.notificationSystem } />
                        }
                    { (this.state.TrackingCode != null ? <AlertBox context={ this } type="success" isImportant hasIcon>کد پیگیری: { this.state.TrackingCode }</AlertBox> : null) }
                </Card> 
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
