import { Component } from "react";
import React from "react";
import { Card, DropDown, Helpers, TextBox, CheckBox } from "core";
import NotificationSystem from "react-notification-system";
import { HandleError } from "../utils/generals"
import Constants from '../utils/constants'

export default class PrescribedThresholdSection extends Component
{
    constructor(props)
    {
        var state = {
            isCommercial: false,
            updateKey: 1
        };

        super(props);
        console.log("record: ", this.props);
        if(this.props.record != null)
        {
            state["personTypeId"] = this.props.record.personTypeId.toString();
            state["ageScopeId"] = (this.props.record.ageScopeId == null ? null : this.props.record.ageScopeId.toString());
            state["transactionTypeId"] = this.props.record.transactionTypeId.toString();
            state["isCommercial"] = this.props.record.isCommercial;
            state["amountThreshold"] = this.props.record.amountThreshold;
        }
        this.state = state;
        this.notificationSystem = React.createRef();

        this.onSubmitSuccess = this.onSubmitSuccess.bind(this);
        this.isAgeScopeDisabled = this.isAgeScopeDisabled.bind(this);
    }

    isAgeScopeDisabled()
    {
        return (this.props.record != null && this.props.record.ageScopeId == null) ||
               (this.state.personTypeId == Constants.PersonTypes.Legal || this.state.personTypeId == null || this.state.personTypeId == '');
    }

    // Events.
    onSubmitSuccess(response)
    {
        if(this.props.record == null)
            this.setState({
                personTypeId: null,
                ageScopeId: null,
                transactionTypeId: null,
                isCommercial: false,
                amountThreshold: null,
                updateKey: this.state.updateKey + 1
            });

        Helpers.DisplaySuccessMessage(this.notificationSystem, response.message);
    }

    render()
    {
        return (
            <>
                <Card context={ this } title={ this.props.record == null ? "جزئیات افزودن آستانه مقرر" : "جزئیات ویرایش آستانه مقرر" } name="crdThreshold"
                    statusColor="primary" hasDefaultSaveButton onSubmitSuccess={ this.onSubmitSuccess } updateKey={ this.state.updateKey }
                    submitUrl={ this.props.record == null ? "/api/amls/PrescribedThreshold/AddPrescribedThreshold" : "/api/amls/PrescribedThreshold/UpdatePrescribedThreshold" }
                    hasBackButton icon="layers-difference" onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)}>

                    <DropDown context={ this } name="personTypeId" title="نوع شخص" options={ this.props.dropDowns.personTypes } required />
                    <DropDown context={ this } name="ageScopeId" title="سن" options={ this.props.dropDowns.ageScopeId } required disabled={ this.isAgeScopeDisabled() }/>
                    <DropDown context={ this } name="transactionTypeId" title="دوره تراکنش" options={ this.props.dropDowns.amountThresholdId } required />
                    <CheckBox context={ this } name="isCommercial" title="سپرده تجاری" required  />
                    <TextBox context={ this } name="amountThreshold" title="آستانه مقرر" required commaSeparated minLength={ 1 } maxLength={ 15 }
                        validateHandler={[ Helpers.NumberTypeValidator, Helpers.MinMaxLengthValidator ]} leftText="ریال" />

                </Card>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
