import { Component } from "react";
import React from "react";
import { PageMain, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import PersonInfSection from '../../sections/personInfSection'
import { GetUrl, HandleError, GetDropDownValues, DownloadBlobFile } from "../../utils/generals";
import Constants from '../../utils/constants'
export default class PersonInfAddPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: true,
            dropDowns: null
        };

        this.notificationSystem = React.createRef();
        this.downloadPersonInfoReport = this.downloadPersonInfoReport.bind(this);
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/PersonInfo/GetAddPageData',
            { },
            successfulResponse =>
            {
                this.setState({
                    dropDowns: {
                        states: GetDropDownValues(Constants.DropDowns.PersonStateId, successfulResponse.data),
                        personTypes: GetDropDownValues(Constants.DropDowns.PersonTypeId, successfulResponse.data),
                        personNationalities: GetDropDownValues(Constants.DropDowns.PersonNationalityId, successfulResponse.data)
                    }
                });
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    downloadPersonInfoReport()
    {
        this.setState({ isLoading: true });
        Helpers.CallServer(`/api/amls/PersonInfo/PersonInfoReport`,
        {},
        successfulResponse => DownloadBlobFile(successfulResponse, "Person Info Report.xlsx"),
        errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
        () => this.setState({
        isLoading: false,
        }), 'GET', 'blob');
    }

    render()
    {
        return (
            <>
                <PageMain title="افزودن اطلاعات شخص" icon="user-plus" footerLinks={[{ href: "/", text: "صفحه اصلی" }]} isLoading={ this.state.isLoading }
                topButtons={[
                    {
                        title: "لیست اشخاص",
                        url: GetUrl("personInfListPage"),
                        color: "outline-primary",
                        icon: "users",
                    },
                    {
                        title: "دانلود نمونه فایل",
                        url: this.downloadPersonInfoReport,
                        color: "outline-success",
                        icon: "notebook",
                    },
                ]}>
                {
                    this.state.dropDowns && <PersonInfSection record={ null } dropDowns={ this.state.dropDowns } />
                }
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
