import { Component } from "react";
import React from "react";
import { AutoComplete, Card, DropDown, Helpers, DatePicker } from "core";
import NotificationSystem from "react-notification-system";
import { HandleError } from "../utils/generals"

export default class RegionInfoSection extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            regionTypes: []
        };

        this.notificationSystem = React.createRef();
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/RegionInfo/GetRegionTypes',
            { },
            successfulResponse =>
            {
                this.setState({ regionTypes: successfulResponse.data });
            },
            errorResponse => {
                return HandleError(Helpers, this.notificationSystem, errorResponse);
            },
            () => { }, 'GET');
    }
    
    render()
    {
        return (
            <>
            <Card context={ this } title={ this.props.record == null ? "جزئیات افزودن مناطق" : "جزئیات ویرایش مناطق" } name="crdRegionInfo" icon="row-insert-top" hasBackButton statusColor="primary" hasDefaultSaveButton
                submitUrl={ this.props.record == null ? "/api/amls/RegionInfo/AddRegionInfo" : "/api/amls/RegionInfo/UpdateRegionInfo" }
                onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)} //onSubmitSuccess={ this.onSubmitSuccess }
                >


                <AutoComplete context={ this } name="regionCode" title="منطقه" notificationSystem={ this.notificationSystem } required
                                        remoteUrl="/api/amls/RegionInfo/SearchRegions"
                                         /> 

                <DropDown context={ this } name="regionTypeId" title="نوع منطقه" options={ this.state.regionTypes } required />
                
                <DatePicker context={ this } name="exporationDateFrom" title="تاریخ اعتبار از" useEpoch required />

            </Card>
            <NotificationSystem ref={this.notificationSystem} />
            </>
        );
    }
}
