import React from "react";
import { PageMain, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import RequestListSection from "../sections/customerInfo/requestListSection";
import { GetUrl, HandleError } from "../utils/generals";

class HomePage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: true,
      dashborad: {
        currentBranch: null,
        requestCount: 0,
      },
    };
    this.notificationSystem = React.createRef();
  }

  componentDidMount() {
    const _this = this;
    
    Helpers.CallServer(
      "/api/FRQST/Request/GetRequestCount",
      { codeChallenge: Helpers.GenerateRandomString(16) },
      (res) => _this.setState({ dashborad: res }),
      (errorResponse) => {
        return HandleError(Helpers, this.notificationSystem, errorResponse);
      },
      () => _this.setState({ isLoading: false })
    );
  }

  render() {
    return (
      <>
        <PageMain
          title=" سامانه جامع مدیریت درخواست های اعتباری"
          icon="home"
          context={ this }
          isLoading={ this.state.isLoading }>
          <div className="row">
            <div className="col-sm-6 col-lg-6">
              <div className="card">
                <div className="card-status-top bg-success"></div>
                <div className="card-body">
                  <div className="d-flex align-items-center">
                    <div className="col-auto">
                      <span className="bg-success text-white avatar">
                        <i className="ti me-2 ti-file-report" />
                      </span>
                    </div>
                    <div className="col ms-3">
                      <h3 className="mb-0">
                        {Helpers.ConvertNumbers2Persian(
                          Helpers.FormatNumbers(
                            this.state.dashborad &&
                              this.state.dashborad.currentBranch
                          )
                        )}
                      </h3>
                      <span className="text-muted text-center">
                          شعبه
                        </span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="col-sm-6 col-lg-6">
              <div className="card">
                <div className="card-status-top bg-info"></div>
                <div className="card-body">
                  <div className="d-flex align-items-center">
                    <div className="col-auto">
                      <span className="bg-info text-white avatar">
                        <i className="ti me-2 ti-file-description"></i>
                      </span>
                    </div>
                    <div className="col ms-3">
                      <h3 className="mb-0">
                        {Helpers.ConvertNumbers2Persian(
                          Helpers.FormatNumbers(
                            this.state.dashborad &&
                              this.state.dashborad.requestCount
                          )
                        )}
                      </h3>
                      <a href={ GetUrl("requestList") }>
                        <span className="text-muted text-center">
                          تعداد درخواست ها
                        </span>
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <RequestListSection hideGridOptions={ true } />
        </PageMain>
        <NotificationSystem ref={ this.notificationSystem } />
      </>
    );
  }
}

export default HomePage;
