import React from "react"
import LegalCustomerInBranch from '../../sections/customerInfo/legalCustomerInBranchSection';
import { Helpers, PageMain } from "core";
import NotificationSystem from 'react-notification-system';
import { GetUrl, HandleError } from "../../utils/generals";

export default class LegalCustomerInBranchViewPage extends React.Component
{
    constructor(props)
    {
        super(props);
        this.state = { isLoading: true };
        this.requestId = this.props.match.params.id;
        this.notificationSystem = React.createRef();
    }

    componentDidMount()
    {
        Helpers.CallServer('/api/FRQST/LegalCustomer/GetLegalCustomerInfo',
        { id: this.requestId },
        successfulResponse =>
        {
            this.setState({ LegalCustomerInfo: successfulResponse.data });
        },
        errorResponse => {
            return HandleError(Helpers, this.notificationSystem, errorResponse);
        },
        () =>
        {
            this.setState({ isLoading: false });
            console.log('Done');
        })
    }

    render() {
        return (
            <>
                <PageMain
                        title="مشاهده درخواست مشتری"
                        icon="list"
                        isLoading={ this.state.isLoading }
                        topButtons={[{ title: "فهرست درخواست", icon: "list-details", color: "outline-success", url: GetUrl("requestList") } ]}>
                            <LegalCustomerInBranch readOnly={ true } legalCustomerInfo={ this.state.LegalCustomerInfo } requestId={ this.requestId } notificationSystem={ this.notificationSystem }/>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
};;
