import React from 'react';
import { PageMain, Helpers } from "core";
import NotificationSystem from 'react-notification-system';
import DropDownSection from '../../sections/basePage/dropDownSection';
import { HandleError } from '../../utils/generals';

export default class DropDownEditPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            DropDown: null,
            isLoading: true
        };
        this.notificationSystem = React.createRef();

        this.dropDownId = this.props.match.params.dropDownId;
    }

    componentDidMount()
    {
        Helpers.CallServer('/api/FRQST/DropDown/GetDropDown',
        { Id: this.dropDownId },
        successfulResponse =>
        {
            this.setState({ DropDown: successfulResponse.data });
        },
        errorResponse => {
            return HandleError(Helpers, this.notificationSystem, errorResponse);
        },
        () =>
        {
            this.setState({ isLoading: false });
        })
    }

    render() {
        return this.state.DropDown != null && (
            <>
                <PageMain
                        isLoading={ this.state.isLoading }
                        title="ویرایش داده های لیستی"
                        icon="layout-list"
                        context={ this }
                        footerLinks={[{ href: "/", text: "صفحه اصلی" },{ href: "https://banksepah.ir", text: "وب سایت رسمی بانک سپه" }]}>
                    <DropDownSection dropDown={ this.state.DropDown }/>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
