import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid, Helpers, Delimiter, DropDown } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from "../../utils/generals";
import { GetGridHeaders } from "../../utils/indicators"
import Constants from '../../utils/constants'

const ButtonWidth =
{
  "width": "100px",
  "height": "30px",
  //"align-self": "center",
}

export default class IndicatorsResultPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: true,
            indicators: [],
            indicatorId: null,
            gridHeaders: [],
            gridUpdate: 1
        };

        this.notificationSystem = React.createRef();

        this.onRoleSelected = this.onRoleSelected.bind(this);
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/DistributionOfIndicator/GetAllIndexes',
            { },
            successfulResponse =>
            {
                this.setState({ indicators: successfulResponse.data });
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    Backward(e)
    {
        e.preventDefault();
        history.back();
    }

    onRoleSelected(item)
    {
        if(item == null || item.value == null || item.value == "")
        {
            this.setState({ gridHeaders: [] });
            return;
        }
        this.setState({
            gridHeaders: GetGridHeaders(item.value),
            gridUpdate: this.state.gridUpdate + 1
        });
    }

    render()
    {
        return (
            <>
                <PageMain context={ this } title="مشاهده نتایج اجرای شاخص‌ها" icon="list-search" isLoading={ this.state.isLoading } footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                topButtons={[]}>
                <div className="card">
                    <div className="card-body">
                        <div className="row">
                            <div className="col-10">
                                <DropDown context={ this } name="indicatorId" title="شاخص" options={ this.state.indicators } required changeHandler={ this.onRoleSelected } />
                            </div>
                            <div className="col-2 p-2 d-flex">
                                <a className="btn btn-sm btn-outline-primary align-self-center text-center" onClick={ this.onPersonChange } style={ ButtonWidth }>
                                    <i className="ti me-2 ti-exchange ml-5" />دانلود
                                </a>
                            </div>
                        </div>
                        {
                            this.state.indicatorId && 
                                <>
                                    <Delimiter></Delimiter>
                                    <br />
                                    <SimpleGrid context={ this } dataKey="id" minHeightTableWrapper={ Constants.MinGridHeight } hasSearchBox={ false } hideExportButton
                                        filterBgColor="primary" name="crdRegionList" title="مشاهده نتایج اجرای شاخص‌ها" notificationSystem={ this.notificationSystem }
                                        enableAutoFetch extraItemsSentToFetchData={{ IndexValue: this.state.indicatorId }}
                                        listApiUrl="/api/amls/bi/Role_01_/SearchRoles" headers={ this.state.gridHeaders } key={ this.state.gridUpdate }
                                        filterOptions={[
                                            { type: 'datepicker', name: 'fromTransactionDate', title: 'از تاریخ', useEpoch: true },
                                            { type: 'datepicker', name: 'toTransactionDate', title: 'تا تاریخ', useEpoch: true }
                                        ]} 
                                        />
                                </>
                        }
                    </div>
                    <div className="card-footer">
                        <div className="d-flex">
                            <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => this.Backward(e) } href="/" title="بازگشت به صفحه قبل">
                            بازگشت
                            <i className="ti ti-arrow-left ms-1"></i>
                            </a>
                        </div>
                        </div>
                    </div>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
