import React from "react";
import { Helpers, Card, CheckBox } from "core";
import NotificationSystem from "react-notification-system";

class FurtherInformationPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tabNumber: 0,
      isLoading: false,
      updateKey: 1,
    };
    this.tabNumber = props.tabNumber;
    this.pageInfo = {
      title: "اطلاعات تکمیلی",
      icon: "circle-plus",
    };
    this.notificationSystem = React.createRef();
  }
  render() {
    return (
      <>

        <Card
          context={this}
          title={this.pageInfo.title}
          icon="file-text"
          //  submitUrl={
          //    this.isNew
          //      ? "/api/Insp/UserRole/Post"
          //       : "/api/Insp/UserRole/Update"
          //    }
          hasBackButton={true}
          statusColor="primary"
          onSubmitError={(error) => handleError(error, this.notificationSystem)}
          hasDefaultSaveButton={true}
          onSubmitSuccess={(e) => this.onSubmitSuccess(e)}
          key={this.state.updateKey}
        >

          <CheckBox
            context={this}
            lgCol={6}
            mdCol={6}
            name="selected"
            title="وضیعت پیوست دارد" />
        </Card>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}

export default FurtherInformationPage;
