import { Component } from "react";
import React from "react";
import { AutoComplete, Card, TextBox, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import { HandleError } from "../utils/generals"

export default class RegionInfoSection extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {};

        this.notificationSystem = React.createRef();
    }

    render()
    {
        return (
            <>
            <Card context={ this } title="ضریب محرومیت " name="deprivation" icon="row-insert-top" hasBackButton statusColor="primary" hasDefaultSaveButton
                //submitUrl={ this.props.record == null ? "/api/brs/BranchDeprivationFactor/AddBranchDepraivationFactor" : "/api/brs/BranchDeprivationFactor/UpdateBranchDepraivationFactor" }
                onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)} //onSubmitSuccess={ this.onSubmitSuccess }
                >


                <AutoComplete context={ this } name="regionCode" title="منطقه" notificationSystem={ this.notificationSystem } required
                                        //remoteUrl="/api/brs/CalculationPeriod/GetConfirmedCalculationPeriods"
                                         /> 

                <DropDown context={ this } name="regionTypeId" title="نوع منطقه" options={ [] } required />
                
                <DatePicker context={ this } name="exporationDateFrom" title="تاریخ اعتبار از" useEpoch required />

            </Card>
            <NotificationSystem ref={this.notificationSystem} />
            </>
        );
    }
}
