import React from "react";
import { PageMain, Helpers, Card, TextBox,  Delimiter, FileUploader} from "core";
import NotificationSystem from "react-notification-system";

class IdentityReporterPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tabNumber: 0,
      isLoading: false,
      updateKey: 1,
    };
    this.tabNumber = props.tabNumber;
    this.pageInfo = {
      title: "اطلاعات هویتی گزارش دهنده",
      icon: "circle-plus",
    };
    this.notificationSystem = React.createRef();
  }
  render() {
    return (
      <>

        <Card
          context={this}
          title={this.pageInfo.title}
          icon="file-text"
          //  submitUrl={
          //    this.isNew
          //      ? "/api/Insp/UserRole/Post"
          //       : "/api/Insp/UserRole/Update"
          //    }
          hasBackButton={true}
          statusColor="primary"
          onSubmitError={(error) => handleError(error, this.notificationSystem)}
          hasDefaultSaveButton={true}
          onSubmitSuccess={(e) => this.onSubmitSuccess(e)}
          key={this.state.updateKey}
        >
       
            <h3>واحد / شعبه گزارش دهنده</h3>
            <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="نام"
              notificationSystem={this.notificationSystem}
              required
            />
            <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="سمت"
              notificationSystem={this.notificationSystem}
              required
            />
            <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="نام خانوادگی"
              notificationSystem={this.notificationSystem}
              required
            />
                <FileUploader
                    context={this}
                    name="ful"
                    title="امضا"
                    validExtensions={['.jpg','.gif','.png','.jpeg']}
                  />
            <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="کد ملی"
              notificationSystem={this.notificationSystem}
              required
            />
          <Delimiter></Delimiter>
          
            <h3>واحد مبارزه با پولشویی</h3>
            <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="نام"
              notificationSystem={this.notificationSystem}
              required
            />
            <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="سمت"
              notificationSystem={this.notificationSystem}
              required
            />
            <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="نام خانوادگی"
              notificationSystem={this.notificationSystem}
              required
            />
            <FileUploader
                    context={this}
                    name="ful"
                    title="امضا"
                    validExtensions={['.jpg','.gif','.png','.jpeg']}
                  />
            <TextBox
              context={this}
              lgCol={6}
              mdCol={12}
              name="age"
              title="کد ملی"
              notificationSystem={this.notificationSystem}
              required
            />
        </Card>

        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}

export default IdentityReporterPage;
