import { Component } from "react";
import React from "react";
import { PageMain, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import PersonInfSection from '../../sections/personInfSection'
import { GetUrl, HandleError } from "../../utils/generals";

export default class PersonInfEditPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            PersonnelInfo: null,
            isLoading: true
        };

        this.notificationSystem = React.createRef();

        this.personInfId = this.props.match.params.personInfId;
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/PersonInfo/GetEditPageData/' + this.personInfId,
            { },
            successfulResponse =>
            {
                this.setState({
                    record: successfulResponse.data.personInfo,
                    
                });
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    render()
    {
        return (
            <>
                <PageMain title="ویرایش اطلاعات شخص" icon="user-check" footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                topButtons={[
                    {
                        title: "لیست اشخاص",
                        url: GetUrl("personInfListPage"),
                        color: "outline-primary",
                        icon: "users",
                    },
                ]}>
                <PersonInfSection record={ this.state.PersonnelInfo } />
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
