import { DatePicker, DropDown, Helpers, PageMain, SimpleGrid } from "core";
import React, { Component } from "react";
import constants from "../../utils/constants";
import NotificationSystem from "react-notification-system";
import Button from "../../componet/button";
import { HandleError, DownloadBlobFile } from "../../utils/generals";
import { Backward } from "../../utils/generals";

export default class BaseDataSection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      updateKey: 1,
      gridHeaders: [],
      reportTypes: null,
      selectedReport: null,
      gridFilterOption: null,
    };

    this.currentSelectedReport = null;
    this.notificationSystem = React.createRef();
  }

  // Events.
  componentDidMount() {
    Helpers.CallServer(
      "/api/amls/TableBaseCard/GetBasicInformation_ReportTypes",
      {},
      (successfulResponse) => {
        this.setState({
          reportTypes: successfulResponse,
        });
      },
      (errorResponse) =>
        HandleError(Helpers, this.notificationSystem, errorResponse),
      () => {
        this.setState({ isLoading: false });
      },
      "GET"
    );
  }

  onSearch = () => {
    var reportTypeId = this.state.reportTypeId;
    // var fromDate = this.state.fromDate;
    // var toDate = this.state.toDate;

    if (reportTypeId == null || reportTypeId == "") {
      Helpers.DisplayErrorMessage(
        this.notificationSystem,
        null,
        "نوع گزارش را وارد نمایید"
      );
      return false;
    }
    // if (fromDate == null || fromDate == "") {
    //   Helpers.DisplayErrorMessage(
    //     this.notificationSystem,
    //     null,
    //     "از تاریخ را وارد نمایید"
    //   );
    //   return false;
    // }
    // if (toDate == null || toDate == "") {
    //   Helpers.DisplayErrorMessage(
    //     this.notificationSystem,
    //     null,
    //     "تا تاریخ را وارد نمایید"
    //   );
    //   return false;
    // }
    this.setState({ isLoading: true });
    Helpers.CallServer(
      `/api/amls/TableBaseCard/GetSimpleGridOptions/${this.currentSelectedReport.value}/${this.props.isLatin}`,
      {},
      (successfulResponse) => {
        this.setState({
          gridHeaders: successfulResponse.headers,
          gridFilterOption: successfulResponse.filterOptions,
          selectedReport: this.currentSelectedReport,
        });
      },
      (errorResponse) => {
        this.setState({
          gridHeaders: null,
          selectedReport: item,
        });
        return HandleError(Helpers, this.notificationSystem, errorResponse);
      },
      () => {
        this.setState({ isLoading: false });
      },
      "GET"
    );
  };

  onRemoveFilter = () => {
    this.setState({
      reportTypeId: null,
      updateKey: this.state.updateKey + 1,
      selectedReport: null,
      fromDate: null,
      toDate: null,
    });
  };

  onReportTypeSelected = (item) => {
    if (item == null || item.value == null || item.value == "") {
      this.currentSelectedReport = null;
      return;
    }
    this.currentSelectedReport = item;
  };

  downloadSampleFile = () => {
    var reportTypeId = this.state.reportTypeId;
    // var fromDate = this.state.fromDate;
    // var toDate = this.state.toDate;
    if (reportTypeId == null || reportTypeId == "") {
      Helpers.DisplayErrorMessage(
        this.notificationSystem,
        null,
        "نوع گزارش را وارد نمایید"
      );
      return false;
    }
    // if (fromDate == null || fromDate == "") {
    //   Helpers.DisplayErrorMessage(
    //     this.notificationSystem,
    //     null,
    //     "از تاریخ را وارد نمایید"
    //   );
    //   return false;
    // }
    // if (toDate == null || toDate == "") {
    //   Helpers.DisplayErrorMessage(
    //     this.notificationSystem,
    //     null,
    //     "تا تاریخ را وارد نمایید"
    //   );
    //   return false;
    // }
    this.setState({ isLoading: true });
    Helpers.CallServer(
      `/api/amls/TableBaseCard/DownloadBasicInformationReport/${this.state.reportTypeId}/${this.props.isLatin}`,
      {},
      (successfulResponse) =>
        DownloadBlobFile(successfulResponse, "BasicInformationReport.xlsx"),
      (errorResponse) =>
        HandleError(Helpers, this.notificationSystem, errorResponse),
      () =>
        this.setState({
          isLoading: false,
        }),
      "GET",
      "blob"
    );
  };

  render() {
    return (
      <>
        <PageMain
          title="گزارش اطلاعات پایه"
          icon="list-search"
          isLoading={this.state.isLoading}
          footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
          key={this.state.updateKey}
        >
          <div className="card">
            <div className="card-body">
              {this.state.reportTypes && (
                <>
                  <div className="row">
                    <DropDown
                      context={this}
                      mdCol={4}
                      lgCol={4}
                      name="reportTypeId"
                      title="نوع گزارش"
                      options={this.state.reportTypes}
                      required
                      changeHandler={(e) => this.onReportTypeSelected(e)}
                    />
                    <DatePicker
                      context={this}
                      mdCol={4}
                      lgCol={4}
                      name="fromDate"
                      title="از تاریخ"
                      useEpoch
                      //  required
                    />
                    <DatePicker
                      context={this}
                      mdCol={4}
                      lgCol={4}
                      name="toDate"
                      title="تا تاریخ"
                      useEpoch
                      // required
                    />
                  </div>
                  <div className="row">
                    <div className="col">
                      <Button
                        buttonClass={`btn btn-outline-primary m-1 styleButton`}
                        text="جستجو"
                        onclick={() => this.onSearch()}
                      />
                      <Button
                        buttonClass={`btn btn-outline-danger m-1 styleButton`}
                        text="حذف فیلتر"
                        onclick={() => this.onRemoveFilter()}
                      />
                      <Button
                        buttonClass={`btn btn-outline-success m-1 styleButton`}
                        text="خروجی اکسل"
                        onclick={() => this.downloadSampleFile()}
                      />
                    </div>
                  </div>
                  <br />
                  <br />
                </>
              )}
              {this.state.selectedReport && (
                <SimpleGrid
                  context={this}
                  minHeightTableWrapper={constants.MinGridHeight}
                  filterBgColor="primary"
                  hideExportButton
                  name="baseInformationList"
                  title={`گزارش اطلاعات پایه - ${this.state.selectedReport.title}`}
                  listApiUrl="/api/amls/TableBaseCard/SearchReport"
                  extraItemsSentToFetchData={{
                    reportTypeId: this.state.selectedReport.value,
                  }}
                  headers={this.state.gridHeaders}
                  enableAutoFetch
                  filterOptions={this.state.gridFilterOption}
                  notificationSystem={this.notificationSystem}
                />
              )}
            </div>
            <div className="card-footer">
              <div className="d-flex">
                <a
                  className="btn btn-outline-secendery ms-auto"
                  onClick={(e) => Backward(e)}
                  href="/"
                  title="بازگشت به صفحه قبل"
                >
                  بازگشت
                  <i className="ti ti-arrow-left ms-1"></i>
                </a>
              </div>
            </div>
          </div>
        </PageMain>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}
