import { Card, AutoComplete,DatePicker, Table, PageMain, Helpers, DropDown } from "core";
import React from "react";
import NotificationSystem from "react-notification-system";
import Constants from "../../utils/constants";
import { HandleError, IsValid } from '../../utils/generals'
import TableItem from '../../models/tableItem'

const Controls =
{
  PersonCode: "personCode",
  RoleCode: "roleCode",
  ExpirationDateFrom: "expirationDateFrom"
}

const ButtonWidth =
{
  "width": "100px"
}

export default class DistributionOfIndicatorsPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: true,
      indexes: null,
      personIndexes: [],
      updateKey: 1,
      records: [],
      laodTable: true
    };

    this.notificationSystem = React.createRef();
    this.RowIndex = 1;
    this.Role = null;
    this.person = null;

    this.onPersonSelected = this.onPersonSelected.bind(this);
    this.LoadTable = this.LoadTable.bind(this);
    this.onPersonIndexAdd = this.onPersonIndexAdd.bind(this);
    this.onRoleSelected = this.onRoleSelected.bind(this);
    this.isRecordExist = this.isRecordExist.bind(this);
    this.onPersonChange = this.onPersonChange.bind(this);
    this.onBeforeSubmit = this.onBeforeSubmit.bind(this);
    this.onDeletePersonIndex = this.onDeletePersonIndex.bind(this);
  }

  GetTableRow(record)
  {
    var tableColumns = [];
    var extraData = {
      Id: record.id,
      ExpirationDateFrom: record.expirationDateFrom,
      Index: this.RowIndex 
    };
    var sRecord = {
      id: record.id,
      personCode: record.personCode,
      roleCode: record.roleCode,
      expirationDateFrom: record.expirationDateFrom,
      Index: this.RowIndex
    };

    tableColumns.push(new TableItem(Controls.RoleCode, record.roleCode, extraData));
    tableColumns.push(new TableItem("roleName", record.roleName, extraData));
    tableColumns.push(new TableItem("solarExpirationDateFrom", record.solarExpirationDateFrom, extraData));

    this.RowIndex++;
    return {
      TableRow: tableColumns,
      Record: sRecord
    };
  }

  LoadTable(records)
  {
    if(this.state.laodTable == false)
      return;
    var personIndexes = [];
    var sRecords = [];

    this.RowIndex = 1;
    if(records == null)
    {
      this.setState({ personIndexes: personIndexes });
      return;
    }

    records.forEach(record =>
    {
      var row = this.GetTableRow(record);

      personIndexes.push(row.TableRow);
      sRecords.push(row.Record);
    });
    this.setState({
      personIndexes: personIndexes,
      records: sRecords,
      laodTable: false,
      updateKey: this.state.updateKey + 1
     });
  }

  isRecordExist(personCode, roleCode)
  {
    var records = this.state.records;
    var result = records.filter(rd => rd.personCode == personCode && rd.roleCode == roleCode);

    if(result != null && result.length >= 1)
      return true;

    return false;
  }

  // Events.
  componentDidMount()
  {
      Helpers.CallServer('/api/amls/DistributionOfIndicator/GetAllIndexes',
          { },
          successfulResponse =>
          {
              this.setState({ indexes: successfulResponse.data });
          },
          errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
          () => { this.setState({ isLoading: false }) }, 'GET');
  }

  onPersonSelected(item)
  {
    if(item == null || item.value == null || item.value == "")
    {
      this.person = null;
      this.LoadTable([]);
      return;
    }
    var personCode = item.value;

    this.person = item;
    Helpers.CallServer('/api/amls/DistributionOfIndicator/GetPersonDistributionOfIndicators/' + personCode,
    { },
    successfulResponse =>
    {
        this.LoadTable(successfulResponse.data);
    },
    errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
    () => { this.setState({ isLoading: false }) }, 'GET');
  }

  onPersonIndexAdd()
  {
    if(!IsValid(Controls.PersonCode, this.state, Helpers, this.notificationSystem))
      return;
    if(!IsValid(Controls.RoleCode, this.state, Helpers, this.notificationSystem))
      return;
    if(!IsValid(Controls.ExpirationDateFrom, this.state, Helpers, this.notificationSystem))
      return;
    if(this.Role == null)
    {
      Helpers.DisplayErrorMessage(this.notificationSystem, null, 'اطلاعات شاخص انتخاب شده پیدا نشد');
      return;
    }
    var expirationDateFrom = this.state[Controls.ExpirationDateFrom];
    var record = {
      id: null,
      personCode: this.state[Controls.PersonCode],
      roleCode: this.state[Controls.RoleCode],
      roleName: this.Role.label,
      expirationDateFrom: expirationDateFrom,
      solarExpirationDateFrom: Helpers.ToPersianJustDate(expirationDateFrom)
    };

    if(this.isRecordExist(record.personCode, record.roleCode))
    {
      Helpers.DisplayErrorMessage(this.notificationSystem, null, 'این داده تکراری می باشد');
      return;
    }
    var personIndexes = this.state.personIndexes;
    var records = this.state.records;
    var row = this.GetTableRow(record);

    personIndexes.push(row.TableRow);
    records.push(row.Record);

    this.setState({
      personIndexes: personIndexes,
      records: records,
      roleCode: null,
      expirationDateFrom: null,
      updateKey: this.state.updateKey + 1,
      laodTable: false
    });
  }

  onRoleSelected(item)
  {
    if(item == null || item.value == null || item.value == "")
    {
      this.Role = null;
      return;
    }
    this.Role = item;
  }

  onPersonChange()
  {
    this.RowIndex = 1;
    this.Role = null;
    this.person = null;
    this.setState({
      [Controls.PersonCode]: null,
      [Controls.RoleCode]: null,
      [Controls.ExpirationDateFrom]: null,
      personIndexes: [],
      laodTable: true,
      updateKey: this.state.updateKey + 1
    });
  }

  onBeforeSubmit()
  {
    var records = this.state.records;
    var newRecords = [];

    records.forEach(rd =>
    {
      if(rd.id == null)
        newRecords.push(rd);
    });

    if(newRecords.length <= 0)
    {
      Helpers.DisplayErrorMessage(this.notificationSystem, null, 'هیچ شاخص جدیدی به پرسنل داده نشده است');
      return false;
    }
    return true;
  }

  onDeletePersonIndex(option)
  {
    var row = option.modalData;
    var tableRowExtraData = row[0].ExtraData;
    var records = this.state.records;
    var personIndexes = this.state.personIndexes;
    var index = tableRowExtraData.Index;

    personIndexes = personIndexes.filter(rd => rd[0].ExtraData.Index != index);
    records = records.filter(rd => rd.Index != index);

    if(tableRowExtraData.Id != null)
    {
      this.setState({ isLoading: true });
      Helpers.CallServer('/api/amls/DistributionOfIndicator/DeleteDistributionOfIndicator/' + tableRowExtraData.Id,
      { },
      successfulResponse =>
      {
        option.modalOpen = false;
        this.setState({
          personIndexes: personIndexes,
          records: records,
          updateKey: this.state.updateKey + 1
        });
      },
      errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
      () => { this.setState({ isLoading: false }) }, 'GET');
    }
    else
    {
      option.modalOpen = false;
      this.setState({
        personIndexes: personIndexes,
        records: records,
        updateKey: this.state.updateKey + 1
      });
    }
  }

  render() {
    return (
      <>
        <PageMain title="توزیع شاخص" icon="chart-line" footerLinks={[{ href: "/", text: "صفحه اصلی" }]} isLoading={ this.state.isLoading } topButtons={[]}>
          {
            this.state.indexes &&
              <Card context={ this } title="جزئیات توزیع شاخص" icon="chart-line" hasDefaultSaveButton hasBackButton statusColor="primary" key={ this.state.updateKey }
                submitUrl="/api/amls/DistributionOfIndicator/AddDistributionOfIndicators" submitFields="records" onBeforeSubmit={ this.onBeforeSubmit }>
              
                <AutoComplete context={ this } name={ Controls.PersonCode } title="پرسنل" required remoteUrl="/api/amls/DistributionOfIndicator/SearchPersonels"
                  changeHandler={ this.onPersonSelected } disabled={ this.state.laodTable ? false : true }
                  externalData={ this.person == null
                                    ? []
                                    : [{ label: this.person.label, value: this.person.value }] } />
                <DropDown context={ this } name={ Controls.RoleCode } title="شاخص" options={ this.state.indexes } required changeHandler={ this.onRoleSelected } />
                <DatePicker context={ this } name={ Controls.ExpirationDateFrom } title="تاریخ اعتبار از" useEpoch required />
                <div className="row">
                  <div className="col-10"></div>
                  <div className="col-1 p-2">
                    <a className="btn btn-sm btn-outline-primary float-end" onClick={ this.onPersonIndexAdd } style={ ButtonWidth }>
                        <i className="ti me-2 ti-circle-plus" />افزودن
                    </a>
                  </div>
                  <div className="col-1 p-2">
                      <a className="btn btn-sm btn-outline-primary float-end" onClick={ this.onPersonChange } style={ ButtonWidth }>
                          <i className="ti me-2 ti-exchange ml-5" />تغییر پرسنل
                      </a>
                  </div>
                </div>
                <Table minHeightTableWrapper={ Constants.MinGridHeight } data={ this.state.personIndexes } hasDeleteButton onDeleteButton={ this.onDeletePersonIndex }
                  headers={[
                    { title: "کد شاخص", name: Controls.RoleCode },
                    { title: "نام شاخص", name: "roleName" },
                    { title: "تاریخ اعتبار از", name: "solarExpirationDateFrom" }
                  ]} />
    
            </Card>
          }
        </PageMain>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}
