import { Component } from "react";
import React from "react";
import { AutoComplete, Card, DropDown, Helpers, CheckBox, TextBox } from "core";
import NotificationSystem from "react-notification-system";
import { HandleError } from "../utils/generals"

export default class RegionInfoSection extends Component
{
    constructor(props)
    {
        var state = {
            updateKey: 1,
            isActive: false
        };

        super(props);

        if(this.props.record != null)
        {
            state['id'] = this.props.record.id;
            state['cityCode'] = this.props.record.cityCode;
            state['regionTypeId'] = this.props.record.regionTypeId.toString();
            state['isActive'] = this.props.record.isActive;
        }
        this.state = state;

        this.notificationSystem = React.createRef();

        this.onSubmitSuccess = this.onSubmitSuccess.bind(this);
        this.onCityChanged = this.onCityChanged.bind(this);
    }

    // Events.
    onSubmitSuccess(response)
    {
        if(this.props.record == null)
            this.setState({
                regionCode: null,
                regionTypeId: null,
                isActive: null,
                updateKey: this.state.updateKey + 1
            });
        Helpers.DisplaySuccessMessage(this.notificationSystem, response.message);
    }

    onCityChanged(e)
    {
        this.setState({
            provinceName: 'نا مشخص',
            //updateKey: this.state.updateKey + 1
        });
        console.log("Province Set");
    }

    render()
    {
        return (
            <>
                <Card context={ this } title={ this.props.record == null ? "جزئیات افزودن مناطق" : "جزئیات ویرایش مناطق" } name="crdRegionInfo" statusColor="primary" hasDefaultSaveButton
                    submitUrl={ this.props.record == null ? "/api/amls/RegionInfo/AddRegionInfo" : "/api/amls/RegionInfo/UpdateRegionInfo" } hasBackButton icon="building"
                    onSubmitError={(error) => HandleError(Helpers, this.notificationSystem, error)} onSubmitSuccess={ this.onSubmitSuccess } updateKey={ this.state.updateKey }>
                    <DropDown context={ this } name="regionTypeId" title="نوع منطقه" options={ this.props.regionTypes } required />
                    <AutoComplete context={ this } name="cityCode" title="شهر" notificationSystem={ this.notificationSystem } required
                        remoteUrl="/api/amls/RegionInfo/SearchCities" changeHandler={ this.onCityChanged }
                        externalData={ this.props.record == null
                            ? []
                            : [{
                                label: this.props.record.cityName,
                                value: this.props.record.cityCode
                            }]}
                            />
                    <TextBox context={ this } name="provinceName" title="نام استان" readOnly />
                    <CheckBox context={ this } name="isActive" title="فعال" />
                </Card>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
