import { SimpleGrid, Helpers } from "core";
import React, { Component } from 'react';
import NotificationSystem from 'react-notification-system';
import { GetUrl, HandleError } from '../../utils/generals'
import Constants from "../../utils/constants";

export default class RequestListSection extends Component {
    
    constructor(props){
        super(props);
        this.state = { } 
        this.notificationSystem = React.createRef();
        
        this.onDataError = this.onDataError.bind(this);
        this.ShowHideMenuByUserRole = this.ShowHideMenuByUserRole.bind(this);
        this.MoreInfoLink = this.MoreInfoLink.bind(this);
        this.GetFilterOptions = this.GetFilterOptions.bind(this);
        this.GetSimpleGridHeaders = this.GetSimpleGridHeaders.bind(this);
        this.ShowHideCloseRequest = this.ShowHideCloseRequest.bind(this);
        this.onDeleteErrorFunc = this.onDeleteErrorFunc.bind(this);

        this.ShowRequestStateFilterOptions = 
                Helpers.IsInRole(Constants.Roles.SuperAdmin) ||
                Helpers.IsInRole(Constants.Roles.SystemAdmin) ||
                Helpers.IsInRole(Constants.Roles.BranchCreditChairman) ||
                Helpers.IsInRole(Constants.Roles.BranchDeputy) ||
                Helpers.IsInRole(Constants.Roles.BranchManager);
    }

    GetFilterOptions()
    {
        if(this.props.hideGridOptions)
            return null;
        var filterOptions = [];

        if(!(Helpers.IsInRole(Constants.Roles.BranchCreditChairman) || Helpers.IsInRole(Constants.Roles.BranchManager) || Helpers.IsInRole(Constants.Roles.BranchDeputy)))
            filterOptions.push({ type: 'auto-complete', name: 'branchCode', title: 'شعبه' , lookupApiUrl:'/api/FRQST/LegalCustomer/GetBranches' });        
        filterOptions.push({ type: 'text', name: 'trackingCode' });
        filterOptions.push({ type: 'text', name: 'nationalCode' });
        filterOptions.push({ type: 'text', name: 'customerNumber' });
        if(this.ShowRequestStateFilterOptions)
            filterOptions.push({ type: 'select', name: 'statusId', title: 'وضعیت درخواست', selectOptions: this.state.RequestStates });

        return filterOptions;
    }

    MoreInfoLink()
    {
        var menueItems = [];

        menueItems.push({ title:'جزییات درخواست', icon:'report-search', url: GetUrl('legalCustomerInBranchView/#replaceWith_id#') });
        menueItems.push({ title:'تکمیل فرم ها', icon:'edit', url: GetUrl('requestFormListPage/#replaceWith_id#') });

        if(Helpers.IsInRole(Constants.Roles.RegionManager) ||
           Helpers.IsInRole(Constants.Roles.BranchCreditChairman) ||
           Helpers.IsInRole(Constants.Roles.BranchManager) ||
           Helpers.IsInRole(Constants.Roles.Center))
            menueItems.push({ title:'مختومه کردن درخواست', icon:'article-off', url: GetUrl('closingRequestAdd/#replaceWith_id#/#replaceWith_name#'), enable: this.ShowHideCloseRequest });
        menueItems.push({ title:'تعیین فرم های تکمیلی', style: "text-info", icon:'checkbox', url: GetUrl('requestFormSelect/#replaceWith_id#'), divider: true, enable: this.ShowHideMenuByUserRole });
        return menueItems;
    }

    GetSimpleGridHeaders()
    {
        var headers = [];

        headers.push({ title: "کد رهگیری", name: "trackingCode" });
        headers.push({ title: "نام شرکت", name: "name" });
        headers.push({ title: "شناسه ملی شرکت", name: "nationalCode" });
        headers.push({ title: "شماره مشتری", name: "customerNumber" });
        headers.push({ title: "شعبه", name: "branchName" });
        headers.push({ title: "وضعیت", name: "requestStatusTitle" });

        if(Helpers.IsInRole(Constants.Roles.SystemAdmin))
            headers.push({ title: "منطقه", name: "areaName" });
        return headers;
    }

    ShowHideMenuByUserRole()
    {
        return Helpers.IsInRole(Constants.Roles.SuperAdmin) ||
               Helpers.IsInRole(Constants.Roles.SystemAdmin) ||
               Helpers.IsInRole(Constants.Roles.BranchCreditChairman) ||
               Helpers.IsInRole(Constants.Roles.RegionManager) ||
               Helpers.IsInRole(Constants.Roles.Center) ||
               Helpers.IsInRole(Constants.Roles.AdminCommercialCredits);
    }

    ShowHideCloseRequest(option)
    {
        return option.requestStatusId == Constants.RequestStatus.RequestApproved.Value || option.requestStatusId == Constants.RequestStatus.RequestRejected.Value;
    }

    onDataError(error)
    {
        return HandleError(Helpers, this.notificationSystem, error.response);
    }

    // Events.
    componentDidMount()
    {
        if(this.ShowRequestStateFilterOptions == false)
            return;
        Helpers.CallServer('/api/FRQST/Request/GetRequestStates',
                            { },
                            successfulResponse =>
                            {
                                this.setState({ RequestStates: successfulResponse.data });
                            },
                            errorResponse => {
                                return HandleError(Helpers, this.notificationSystem, errorResponse);
                            },
                            () => { }, 'GET')
    }

    onDeleteErrorFunc(errorResponse)
    {
        return HandleError(Helpers, this.notificationSystem, errorResponse);
    }

    render() { 
        return (this.state.RequestStates != null || this.ShowRequestStateFilterOptions == false) && (
          <>
            <SimpleGrid context={ this }
                name="grid"
                title="فهرست درخواست ها"
                enableAutoFetch
                listApiUrl="/api/FRQST/Request/GetRequestList"
                dataKey="id"
                moreInfoLinks={ this.MoreInfoLink() }
                headers={ this.GetSimpleGridHeaders() }
                deleteApiUrl={ Helpers.IsInRole(Constants.Roles.SystemAdmin) ? '/api/FRQST/Request/DeleteRequest' : null }
                deleteModalFieldNames={ [ "name", "nationalCode", "customerNumber", "branchName" ] }
                onDeleteErrorFunc={ this.onDeleteErrorFunc }
                badgeOnCells={[
                    {
                        fieldName: "requestStatusTitle",
                        color: (x) => {
                        switch (x) {
                            case Constants.RequestStatus.RequestRejected.Label:
                                case Constants.RequestStatus.Closed.Label:
                                    return "red";
                                case Constants.RequestStatus.WaitingToFillFixedFormsByCustomer.Label:
                                case Constants.RequestStatus.WaitingToCheckOrAssignFormsByCreditBranchManager.Label:
                                case Constants.RequestStatus.WaitingToFillBranchFormsByCustomer.Label:
                                case Constants.RequestStatus.WaitingToCheckRequestByBranchManager.Label:
                                case Constants.RequestStatus.WaitingToCheckOrAssignFormsByRegionManager.Label:
                                case Constants.RequestStatus.WaitingToFillRegionManagerFormsByCustomer.Label:
                                case Constants.RequestStatus.WaitingToCheckOrAssignFormsByCenter.Label:
                                case Constants.RequestStatus.WaitingToFillCenterFormsByCustomer.Label:
                                case Constants.RequestStatus.WaitingToCheckOrAssignFormsByAdminCommercialCredits.Label:
                                case Constants.RequestStatus.WaitingToFillAdminCommercialCreditsFormsByCustomer.Label:
                                    return "yellow";
                                case Constants.RequestStatus.RequestApproved.Label:
                                    return "green";
                                default:
                                    return "gray"
                        }
                        },
                    },
                    ]}
                minHeightTableWrapper={ 200 }
                filterOptions={ this.GetFilterOptions() }
                notificationSystem={ this.notificationSystem }
                onDataError={ this.onDataError } />
            <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
