import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import {
  GetUrl,
  HandleError,
  GetDropDownValues,
} from "../../../utils/generals";
import Constants from "../../../utils/constants";
import { Backward } from "../../../utils/generals";
import {
  centralBankTransactionReportOptions,
  personNumberTypeOptions,
} from "../../../utils/selectOptionsUtils";
export default class CentralBankTransactionReportPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: false,
    };

    this.notificationSystem = React.createRef();
    this.onDeleteErrorFunc = this.onDeleteErrorFunc.bind(this);
  }

  onDeleteErrorFunc(errorResponse) {
    return HandleError(Helpers, this.notificationSystem, errorResponse);
  }

  render() {
    return (
      <>
        <PageMain
          context={this}
          title="گزارش تراکنشی بانک مرکزی"
          icon="list-search"
          isLoading={this.state.isLoading}
          footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
        >
          <div className="card">
            <div className="card-body">
              <SimpleGrid
                context={this}
                dataKey="id"
                minHeightTableWrapper={Constants.MinGridHeight}
                hasSearchBox={false}
                hideExportButton
                filterBgColor="primary"
                name="centralBankTransactionReport"
                title="گزارش تراکنشی بانک مرکزی "
                notificationSystem={this.notificationSystem}
                enableAutoFetch
                listApiUrl="/api/amls/"
                deleteApiUrl="/api/amls/"
                onDeleteErrorFunc={this.onDeleteErrorFunc}
                headers={[{ title: "ردیف", name: "name" }]}
                filterOptions={[
                  {
                    type: "select",
                    name: "reportId",
                    title: "نوع گزارش",
                    selectOptions: centralBankTransactionReportOptions(),
                    // className: "form-required",
                    //required,
                  },

                  {
                    type: "select",
                    name: "personNumberTypeId",
                    title: "نوع شخص",
                    selectOptions: personNumberTypeOptions(),
                    // className: "form-required",
                    //required,
                  },
                  {
                    type: "text",
                    name: "natinalCode",
                    title: "شناسه ملی/کد ملی/اتباع",
                  },
                  {
                    type: "datepicker",
                    name: "fromTransaction",
                    title: " بازده تراکنش  از ",
                    useEpoch: true,
                  },
                  {
                    type: "datepicker",
                    name: "endTransaction",
                    title: " بازده تراکنش  تا ",
                    useEpoch: true,
                  },
                  {
                    type: "datepicker",
                    name: "fromOpeningAccountDate",
                    title: " تاریخ افتتاح حساب  از ",
                    useEpoch: true,
                  },
                  {
                    type: "datepicker",
                    name: "endOpeningAccountDate",
                    title: " تاریخ افتتاح حساب  تا ",
                    useEpoch: true,
                  },
                ]}
              />
            </div>
            <div className="card-footer">
              <div className="d-flex">
                <a
                  className="btn btn-outline-secendery ms-auto"
                  onClick={(e) => Backward(e)}
                  href="/"
                  title="بازگشت به صفحه قبل"
                >
                  بازگشت
                  <i className="ti ti-arrow-left ms-1"></i>
                </a>
              </div>
            </div>
          </div>
        </PageMain>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}
