import React, { Component } from "react";
import NotificationSystem from "react-notification-system";
import { Delimiter, SimpleGrid } from "core";
import constants from "../../utils/constants";
import Button from "../../componet/button";

export default class CausalAndRelativeRelationshipSection extends Component {
  constructor() {
    super();
    this.state = {};
    this.notificationSystem = React.createRef();
  }

  render() {
    return (
      <>
        <div className="card">
          <div className="card-body">
            <SimpleGrid
              context={this.props.parentContext}
              icon="topology-star"
              dataKey="id"
              minHeightTableWrapper={constants.MinGridHeight}
              hasSearchBox={false}
              hideExportButton
              filterBgColor="primary"
              name="customerRelationShip"
              title="روابط"
              enableAutoFetch
              listApiUrl="/api/amls/CausalAndRelativeRelationship/SearchCustomerRelationship"
              headers={this.props.headers}
              filterOptions={null}
              notificationSystem={this.notificationSystem}
            />
          </div>
          <Delimiter style="solid" />
          <div className="row">
            <div className="text-start mb-2 col">
              <Button
                buttonClass={`btn btn-outline-primary col-lg-1.5  m-3`}
                iconLeftClass={`ti ti-arrow-right me-1`}
                text={`مرحله قبلی`}
                onclick={() => this.props.onChangeStepNumber(0)}
              />
            </div>
            <div className="text-end mb-2 col ">
              <Button
                buttonClass={`btn btn-outline-primary col-lg-1.5  m-3`}
                iconRightClass={`ti ti-arrow-left ms-1`}
                text={`مرحله بعدی`}
                onclick={() => this.props.onChangeStepNumber(2)}
              />
            </div>
          </div>
        </div>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}
