import { PageMain, Card, DropDown, DatePicker, Helpers } from "core";
import React, { Component } from "react";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from "../../utils/generals";

export default class BaseDataPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoading: false,
    };

    this.notificationSystem = React.createRef();

    this.onBeforeSubmit = this.onBeforeSubmit.bind(this);
  }

  // Evnets.
  onBeforeSubmit() {
    var reportTypeId = this.state.reportTypeId;
    if (reportTypeId == null || reportTypeId == "") {
      Helpers.DisplayErrorMessage(
        this.notificationSystem,
        null,
        "نوع گزارش را وارد نمایید"
      );
      return false;
    }
    var fromDate = this.state.fromDate;
    if (fromDate == null || fromDate == "") {
      Helpers.DisplayErrorMessage(
        this.notificationSystem,
        null,
        "از تاریخ را وارد نمایید"
      );
      return false;
    }
    var toDate = this.state.toDate;
    if (toDate == null || toDate == "") {
      Helpers.DisplayErrorMessage(
        this.notificationSystem,
        null,
        "تا تاریخ را وارد نمایید"
      );
      return false;
    }
    Helpers.DisplayErrorMessage(
      this.notificationSystem,
      null,
      "این فرم در حال پیاده سازی می باشد"
    );
    return false;
    // Helpers.DisplaySuccessMessage(
    //   this.notificationSystem,
    //   "درخواست با موفقیت انجام شد ، در حال انتقال به فهرست گزارشات ....."
    // );
    // setInterval(() => {
    //   window.location.href = GetUrl(
    //     "baseDataListPage",
    //     this.state.reportTypeId
    //   );
    // }, 3000);
    // return true;
  }

  reportTypeOption = () => {
    let array = [];
    return array.concat([
      {
        title: "مشتری",
        value: 1,
        label: "مشتری",
      },
      { title: "حساب", value: 2, label: "حساب" },
      {
        title: "کارت",
        value: 3,
        label: "کارت",
      },
      {
        title: "شعبه",
        value: 4,
        label: "شعبه",
      },
      {
        title: "خودپرداز",
        value: 5,
        label: "خودپرداز",
      },
      { title: "پین پد", value: 6, label: "پین پد" },
      {
        title: "شرکت پرداخت",
        value: 7,
        label: "شرکت پرداخت",
      },
      {
        title: "ترمینال",
        value: 8,
        label: "ترمینال",
      },
      {
        title: "پذیرنده",
        value: 9,
        label: "پذیرنده",
      },
      { title: "شبای پذیرنده", value: 10, label: "شبای پذیرنده" },
      {
        title: "وکیل موکل",
        value: 11,
        label: "وکیل موکل",
      },
      {
        title: "چک",
        value: 12,
        label: "چک",
      },
      {
        title: "اطلاعات سپرده گذاران بانک",
        value: 13,
        label: "اطلاعات سپرده گذاران بانک",
      },
    ]);
  };

  render() {
    return (
      <>
        <PageMain
          context={this}
          title="اطلاعات پایه"
          icon="database"
          isLoading={this.state.isLoading}
          footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
        >
          <Card
            context={this}
            title="اطلاعات پایه"
            name="crdbaseDareReport"
            statusColor="primary"
            hasDefaultSaveButton
            hasBackButton={false}
            submitUrl=""
            icon="database"
            onSubmitError={(error) =>
              HandleError(Helpers, this.notificationSystem, error)
            }
            onBeforeSubmit={this.onBeforeSubmit}
          >
            <DropDown
              context={this}
              name="reportTypeId"
              title="نوع گزارش"
              options={this.reportTypeOption()}
              required
            />
            <DatePicker
              context={this}
              name="fromDate"
              title="از تاریخ"
              useEpoch
              required
            />
            <DatePicker
              context={this}
              name="toDate"
              title="تا تاریخ"
              useEpoch
              required
            />
          </Card>
        </PageMain>
        <NotificationSystem ref={this.notificationSystem} />
      </>
    );
  }
}
