import { Component } from "react";
import React from "react";
import { PageMain, SimpleGrid, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import { GetUrl, HandleError } from "../../utils/generals";
import Constants from '../../utils/constants'

export default class CustomerActivityLevelListPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {};

        this.notificationSystem = React.createRef();

        this.onDeleteErrorFunc = this.onDeleteErrorFunc.bind(this);
    }

    // Events.
    Backward(e)
    {
        e.preventDefault();
        history.back();
    }

    onDeleteErrorFunc(errorResponse)
    {
        return HandleError(Helpers, this.notificationSystem, errorResponse);
    }

    render()
    {
        return (
            <>
                <PageMain context={ this } title="لیست سطح فعالیت مشتری " icon="list-search" isLoading={ this.state.isLoading } footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                topButtons={[
                    {
                    title: "افزودن سطح فعالیت مشتری",
                    url: GetUrl("customerActivityLevelAddPage"),
                    color: "outline-primary",
                    icon: "user-circle",
                    },
                ]}>
                <div className="card">
                    <div className="card-body">
                        <SimpleGrid context={ this } dataKey="id" minHeightTableWrapper={ Constants.MinGridHeight } hasSearchBox={ false } hideExportButton
                            filterBgColor="primary" name="crdRegionList" title="جزئیات سطح فعالیت مشتری" notificationSystem={ this.notificationSystem }
                            enableAutoFetch listApiUrl="/api/amls/CustomerActivityLevel/SearchCustomersActivityLevel" editPageUrl={ GetUrl("customerActivityLevelEditPage" ) }
                            deleteApiUrl="/api/amls/CustomerActivityLevel/DeleteCustomerActivityLevel" onDeleteErrorFunc={ this.onDeleteErrorFunc }
                            headers={[
                                { title: "نوع شخص", name: "personTypeName" },
                                { title: "تاریخ اعتبار از", name: "expirationDateFromStr" },
                                { title: "سطح فعالیت مجاز", name: "allowedActivityLevel" },
                                { title: "سطح مجاز بالاتر از سطح فعالیت", name: "allowedLevelAboveActivityLevelPercent" },
                                { title: "وضعیت شغل", name: "jobTypeName" },
                                { title: "شغل", name: "jobId" },
                                { title: "وضعیت مالیات", name: "taxStateName" },
                                { title: "صنف", name: "guildId" }
                            ]}
                            filterOptions={[
                                { type: 'select', name: 'personTypeId', title: 'نوع شخص', selectOptions: this.state.dropDowns.personTypes },

                                { type: 'text', name: 'code', title: 'کد' },
                                { type: 'text', name: 'name', title: 'نام' },
                                { type: 'select', name: 'statusId', title: 'وضعیت', selectOptions: this.state.dropDowns.states },
                                
                                { type: 'select', name: 'personNationalityId', title: 'تابعیت', selectOptions: this.state.dropDowns.personNationalities }
                            ]} />
                    </div>
                    <div className="card-footer">
                        <div className="d-flex">
                            <a className="btn btn-outline-secendery ms-auto" onClick={ (e) => this.Backward(e) } href="/" title="بازگشت به صفحه قبل">
                            بازگشت
                            <i className="ti ti-arrow-left ms-1"></i>
                            </a>
                        </div>
                        </div>
                    </div>
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
