import { Component } from "react";
import React from "react";
import { PageMain } from "core";
import NotificationSystem from "react-notification-system";
import RegionInfoSection from '../../sections/regionInfoSection'
import { GetUrl } from "../../utils/generals";

export default class RegionInfoEditPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: true,
            regionTypes: null
        };

        this.notificationSystem = React.createRef();

        this.regionInfoId = this.props.match.params.regionInfoId;
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/RegionInfo/GetRegionInfoPageData',
            { },
            successfulResponse =>
            {
                var rt = (successfulResponse.data.RegionTypes == null ? [] : successfulResponse.data.RegionTypes);

                this.setState({
                    regionTypes: rt,
                    regionInfo: successfulResponse.data.regionInfo
                });
            },
            errorResponse => {
                return HandleError(Helpers, this.notificationSystem, errorResponse);
            },
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    render()
    {
        return this.state.regionTypes && (
            <>
                <PageMain title="ویرایش مناطق" icon="building" footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                topButtons={[
                    {
                        title: "لیست مناطق",
                        url: GetUrl("regionInfoListPage"),
                        color: "outline-primary",
                        icon: "list-search",
                    },
                ]}>
                <RegionInfoSection record={ this.state.regionInfo } regionTypes={ this.state.regionTypes } />
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        )
    }
}
