import { Component } from "react";
import React from "react";
import { PageMain, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import RegionInfoSection from '../../sections/regionInfoSection'
import { GetUrl, HandleError } from "../../utils/generals";

export default class RegionInfoAddPage extends Component{
    constructor(props)
    {
        super(props);

        this.state = {
            isLoading: true,
            regionTypes: null
        };

        this.notificationSystem = React.createRef();
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/RegionInfo/GetRegionTypes',
            { },
            successfulResponse =>
            {
                this.setState({ regionTypes: successfulResponse.data });
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    render()
    {
        return (
            <>
                <PageMain title="افزودن مناطق" icon="building" footerLinks={[{ href: "/", text: "صفحه اصلی" }]} isLoading={ this.state.isLoading }
                topButtons={[
                    {
                        title: "لیست مناطق",
                        url: GetUrl("regionInfoListPage"),
                        color: "outline-primary",
                        icon: "list-search",
                    },
                ]}>
                {
                    this.state.regionTypes && <RegionInfoSection record={ null } regionTypes={ this.state.regionTypes } />
                }
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        )
    }
}
