import { Component } from "react";
import React from "react";
import { PageMain, Helpers } from "core";
import NotificationSystem from "react-notification-system";
import CustomerActivityLevelSection from '../../sections/customerActivityLevelSection'
import { GetUrl, HandleError, GetDropDownValues } from "../../utils/generals";
import Constants from '../../utils/constants'

export default class CustomerActivityLevelEditPage extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {};

        this.notificationSystem = React.createRef();

        this.customerActivityLevelId = this.props.match.params.customerActivityLevelId;
    }

    // Events.
    componentDidMount()
    {
        Helpers.CallServer('/api/amls/CustomerActivityLevel/GetEditPageData/' + this.customerActivityLevelId,
            { },
            successfulResponse =>
            {
                this.setState({
                    record: successfulResponse.data.customerActivityLevel,
                    dropDowns: {
                        personTypes: GetDropDownValues(Constants.DropDowns.PersonTypeId, successfulResponse.data.dropDowns),
                        jobTypes: GetDropDownValues(Constants.DropDowns.JobTypeId, successfulResponse.data.dropDowns),
                        taxStates: GetDropDownValues(Constants.DropDowns.TaxStateId, successfulResponse.data.dropDowns)
                    }
                });
            },
            errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
            () => { this.setState({ isLoading: false }) }, 'GET');
    }

    render()
    {
        return (
            <>
                <PageMain title="ویرایش سطح فعالیت مشتری" icon="user-circle" footerLinks={[{ href: "/", text: "صفحه اصلی" }]}
                topButtons={[
                    {
                        title: "لیست سطح فعالیت مشتری",
                        url: GetUrl("customerActivityLevelListPage"),
                        color: "outline-primary",
                        icon: "list-search",
                    },
                ]}>
                {
                    (this.state.dropDowns != null && this.state.record != null) && <CustomerActivityLevelSection record={ this.state.record } dropDowns={ this.state.dropDowns } />
                }                    
                </PageMain>
                <NotificationSystem ref={ this.notificationSystem } />
            </>
        );
    }
}
