import { Component } from "react";
import React from "react";
import NotificationSystem from "react-notification-system";
import PaiedLoanSection from "../../../sections/projects/paiedLoanSection";
import { PageMain, Helpers } from "core";
import { GetUrl, HandleError } from "../../../utils/generals";
import Constants from '../../../utils/constants'

export default class PaiedLoanViewPage extends Component {
  constructor(props) {
    super(props);
    this.state = { isLoading: true };

    this.notificationSystem = React.createRef();
    this.companyName = null;

    this.id = this.props.match.params.id;
    this.companyInfoId = this.props.match.params.companyInfoId;
    this.companyLoanAccountId = this.props.match.params.companyLoanAccountId;
  }

  // Events.
  componentDidMount()
  {
      Helpers.CallServer('/api/FRQST/PaiedLoan/GetPaidLoanPageDataInReviseMode',
      {
        PaiedLoanId: this.id,
        CompanyInfoId: this.companyInfoId,
        CompanyLoanAccountId: this.companyLoanAccountId
      },
      successfulResponse =>
      {
        var dropDowns = successfulResponse.data.dropDowns;
        var loans = dropDowns.filter(drp => drp.id == Constants.LoanTypes.DropDownId);
        var projects = dropDowns.filter(drp => drp.id == Constants.Projects);
        
        if(successfulResponse.data.legalCompanyInfo != null)
          this.companyName = successfulResponse.data.legalCompanyInfo.companyName;
        this.setState({
          loans: loans == null || loans.length != 1 ? [] : loans[0].values,
          projects: projects == null || projects.length != 1 ? [] : projects[0].values,
          paidLoan: successfulResponse.data.paidLoan
        });
      },
      errorResponse => HandleError(Helpers, this.notificationSystem, errorResponse),
      () =>
      { this.setState({ isLoading: false }) });
  }

  render() {
    return (
      <>
        <PageMain context={ this } title={ "ویرایش ریز تسهیلات دریافتی" + (this.companyName == null ? "" : " (" + this.companyName + ")")}
          icon="cash-banknote" isLoading={ this.state.isLoading }
          topButtons={[
            {
              title: "فهرست ریز تسهیلات پرداختی",
              url: GetUrl("paiedLoanListPage/" + this.companyInfoId + '/' + this.companyLoanAccountId),
              color: "outline-success",
              icon: "list",
            },
          ]}
          footerLinks={[{ href: "/", text: "صفحه اصلی" }]}>
          {
            this.state.projects == null || this.state.loans == null
              ? null
              : <PaiedLoanSection legalCompanyInfoId={ this.companyInfoId }
                                  companyLoanAccountId={ this.companyLoanAccountId }
                                  projects={ this.state.projects}
                                  loans={ this.state.loans }
                                  paidLoan={ this.state.paidLoan } />
          }
        </PageMain>
        <NotificationSystem ref={this.notificationSystem} />
        </>
    );
  }
}
